import React, { useState } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import Pitch from './Pitch';
import Bench from './Bench';

const POSITION_TYPES = {
  PLAYER: 'player',
};

const initialPlayers = [
  { id: '1', name: 'Player 1', position: null },
  { id: '2', name: 'Player 2', position: null },
  { id: '3', name: 'Player 3', position: null },
  { id: '4', name: 'Player 4', position: null },
  // up to 25 players ...
];

function App() {
  const [players, setPlayers] = useState(initialPlayers);

  const onDropPlayer = (playerId, position) => {
    setPlayers((prevPlayers) =>
      prevPlayers.map((p) =>
        p.id === playerId ? { ...p, position: position } : p
      )
    );
  };

  const pitchPlayers = players.filter((p) => p.position !== null);
  const benchPlayers = players.filter((p) => p.position === null);

  return (
    <DndProvider backend={HTML5Backend}>
      <div style={{ display: 'flex', gap: '20px', padding: '20px' }}>
        <Pitch pitchPlayers={pitchPlayers} onDropPlayer={onDropPlayer} />
        <Bench benchPlayers={benchPlayers} onDropPlayer={onDropPlayer} />
      </div>
    </DndProvider>
  );
}

export default App;
