import React, { useState, useEffect } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const ItemTypes = { PLAYER: 'player' };

const dummyLeagues = [
  { id: 'ucl', name: 'UEFA Champions League' },
  { id: 'uel', name: 'UEFA Europa League' },
];

const dummyTeams = [
  { id: 'fenerbahce', name: 'Fenerbahçe', leagueId: 'ucl' },
  { id: 'galatasaray', name: 'Galatasaray', leagueId: 'ucl' },
];

const dummyPlayers = [
  { id: '1', name: 'Altay Bayındır', teamId: 'fenerbahce', isHomegrown: true },
  { id: '2', name: 'Ferdi Kadıoğlu', teamId: 'fenerbahce', isHomegrown: true },
  { id: '3', name: 'Edin Dzeko', teamId: 'fenerbahce', isHomegrown: false },
  { id: '4', name: 'Mauro Icardi', teamId: 'galatasaray', isHomegrown: false },
  { id: '5', name: 'Victor Nelsson', teamId: 'galatasaray', isHomegrown: false },
];

function Player({ player }) {
  const [{ isDragging }, drag] = useDrag({
    type: ItemTypes.PLAYER,
    item: { id: player.id },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  return (
    <div
      ref={drag}
      style={{
        opacity: isDragging ? 0.5 : 1,
        cursor: 'move',
        backgroundColor: player.isHomegrown ? '#4ade80' : '#60a5fa',
        color: 'white',
        padding: '8px 12px',
        margin: '4px',
        borderRadius: 6,
        fontWeight: 'bold',
        textAlign: 'center',
        minWidth: 120,
      }}
    >
      {player.name}
    </div>
  );
}

function DropArea({ children, onDropPlayer, position }) {
  const [, drop] = useDrop({
    accept: ItemTypes.PLAYER,
    drop: (item) => {
      onDropPlayer(item.id, position);
    },
  });

  return (
    <div
      ref={drop}
      style={{
        backgroundColor: '#1e293b',
        border: '2px dashed #334155',
        borderRadius: 10,
        padding: 16,
        minHeight: 200,
        flex: 1,
      }}
    >
      {children}
    </div>
  );
}

function Pitch({ players, onDropPlayer }) {
  return (
    <DropArea onDropPlayer={onDropPlayer} position="pitch">
      <h2 style={{ color: '#fbbf24' }}>Pitch</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
        {players.map((p) => (
          <Player key={p.id} player={p} />
        ))}
      </div>
    </DropArea>
  );
}

function Bench({ players, onDropPlayer }) {
  return (
    <DropArea onDropPlayer={onDropPlayer} position="bench">
      <h2 style={{ color: '#94a3b8' }}>Bench</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
        {players.map((p) => (
          <Player key={p.id} player={p} />
        ))}
      </div>
    </DropArea>
  );
}

export default function App() {
  const [selectedLeague, setSelectedLeague] = useState('');
  const [selectedTeam, setSelectedTeam] = useState('');
  const [playerPositions, setPlayerPositions] = useState({}); // id -> 'pitch' | 'bench'

  const teams = dummyTeams.filter((team) => team.leagueId === selectedLeague);
  const players = dummyPlayers.filter((p) => p.teamId === selectedTeam);

  useEffect(() => {
    if (selectedTeam) {
      const newPositions = {};
      players.forEach((p) => {
        newPositions[p.id] = 'bench';
      });
      setPlayerPositions(newPositions);
    }
  }, [selectedTeam]);

  const pitchPlayers = players.filter((p) => playerPositions[p.id] === 'pitch');
  const benchPlayers = players.filter((p) => playerPositions[p.id] === 'bench');

  const onDropPlayer = (id, toPosition) => {
    setPlayerPositions((prev) => ({ ...prev, [id]: toPosition }));
  };

  // UEFA Validation
  const homegrown = players.filter((p) => p.isHomegrown).length;
  const nonHomegrown = players.length - homegrown;
  const isSquadValid =
    players.length <= 25 && homegrown >= 8 && nonHomegrown <= 17;

  return (
    <DndProvider backend={HTML5Backend}>
      <div
        style={{
          backgroundColor: '#0f172a',
          color: '#fff',
          minHeight: '100vh',
          padding: 20,
          fontFamily: 'sans-serif',
        }}
      >
        <h1 style={{ color: '#60a5fa' }}>UEFA Squad Builder</h1>

        <div style={{ display: 'flex', gap: 12, marginBottom: 20 }}>
          <select
            value={selectedLeague}
            onChange={(e) => {
              setSelectedLeague(e.target.value);
              setSelectedTeam('');
            }}
          >
            <option value="">-- Select League --</option>
            {dummyLeagues.map((l) => (
              <option key={l.id} value={l.id}>
                {l.name}
              </option>
            ))}
          </select>

          <select
            value={selectedTeam}
            onChange={(e) => setSelectedTeam(e.target.value)}
            disabled={!selectedLeague}
          >
            <option value="">-- Select Team --</option>
            {teams.map((t) => (
              <option key={t.id} value={t.id}>
                {t.name}
              </option>
            ))}
          </select>
        </div>

        {!selectedTeam && (
          <p style={{ color: '#cbd5e1' }}>Please select a league and a team.</p>
        )}

        {selectedTeam && (
          <>
            <div style={{ display: 'flex', gap: 20 }}>
              <Pitch players={pitchPlayers} onDropPlayer={onDropPlayer} />
              <Bench players={benchPlayers} onDropPlayer={onDropPlayer} />
            </div>

            <div style={{ marginTop: 30 }}>
              <h3>UEFA Validation</h3>
              <p>Homegrown Players: {homegrown} (Min 8)</p>
              <p>Non-homegrown Players: {nonHomegrown} (Max 17)</p>
              <p>Total Squad Size: {players.length} (Max 25)</p>
              <p>
                <strong style={{ color: isSquadValid ? '#4ade80' : '#f87171' }}>
                  {isSquadValid
                    ? 'Squad meets UEFA requirements!'
                    : 'Squad does not meet UEFA requirements.'}
                </strong>
              </p>
            </div>
          </>
        )}
      </div>
    </DndProvider>
  );
}
