// src/App.js
import React, { useState, useEffect } from 'react';
import './index.css';

const App = () => {
  const initialData = {
    countries: [
      { id: 'turkey', name: 'Turkey' },
      { id: 'england', name: 'England' },
      { id: 'spain', name: 'Spain' },
    ],
    teams: [
      { id: 'fenerbahce', name: 'Fenerbahçe', countryId: 'turkey' },
      { id: 'galatasaray', name: 'Galatasaray', countryId: 'turkey' },
      { id: 'besiktas', name: 'Beşiktaş', countryId: 'turkey' },
      { id: 'manchester_united', name: 'Manchester United', countryId: 'england' },
      { id: 'real_madrid', name: 'Real Madrid', countryId: 'spain' },
    ],
    players: [
      { id: 'player1', name: 'Altay Bayındır', teamId: 'manchester_united', age: 26, nationality: 'Turkish', clubTrained: true, associationTrained: true, position: 'Goalkeeper', isUnder21: false },
      { id: 'player2', name: 'Ferdi Kadıoğlu', teamId: 'fenerbahce', age: 24, nationality: 'Turkish', clubTrained: true, associationTrained: true, position: 'Defender', isUnder21: false },
      { id: 'player3', name: 'Mert Hakan Yandaş', teamId: 'fenerbahce', age: 29, nationality: 'Turkish', clubTrained: false, associationTrained: true, position: 'Midfielder', isUnder21: false },
      { id: 'player4', name: 'Arda Güler', teamId: 'real_madrid', age: 19, nationality: 'Turkish', clubTrained: true, associationTrained: true, position: 'Midfielder', isUnder21: true },
      { id: 'player5', name: 'Çengiz Ünder', teamId: 'fenerbahce', age: 26, nationality: 'Turkish', clubTrained: false, associationTrained: false, position: 'Forward', isUnder21: false },
      { id: 'player6', name: 'Fred', teamId: 'fenerbahce', age: 31, nationality: 'Brazilian', clubTrained: false, associationTrained: false, position: 'Midfielder', isUnder21: false },
      { id: 'player7', name: 'Edin Dzeko', teamId: 'fenerbahce', age: 38, nationality: 'Bosnian', clubTrained: false, associationTrained: false, position: 'Forward', isUnder21: false },
      { id: 'player8', name: 'Victor Nelsson', teamId: 'galatasaray', age: 25, nationality: 'Danish', clubTrained: false, associationTrained: false, position: 'Defender', isUnder21: false },
      { id: 'player9', name: 'Mauro Icardi', teamId: 'galatasaray', age: 31, nationality: 'Argentine', clubTrained: false, associationTrained: false, position: 'Forward', isUnder21: false },
    ],
  };

  const [countries] = useState(initialData.countries);
  const [teams, setTeams] = useState([]);
  const [players, setPlayers] = useState([]);
  const [selectedCountryId, setSelectedCountryId] = useState('');
  const [selectedTeamId, setSelectedTeamId] = useState('');

  useEffect(() => {
    if (selectedCountryId) {
      setTeams(initialData.teams.filter(team => team.countryId === selectedCountryId));
    } else {
      setTeams([]);
    }
    setSelectedTeamId('');
    setPlayers([]);
  }, [selectedCountryId]);

  useEffect(() => {
    if (selectedTeamId) {
      setPlayers(initialData.players.filter(player => player.teamId === selectedTeamId));
    } else {
      setPlayers([]);
    }
  }, [selectedTeamId]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-black text-white font-inter p-6">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-5xl font-bold text-center mb-12 text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-lime-400 drop-shadow-xl">
          ⚽️ Champions League Squad Builder
        </h1>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-10">
          <div className="bg-slate-800 bg-opacity-50 rounded-xl p-6 shadow-lg backdrop-blur-md">
            <label htmlFor="country-select" className="block mb-2 text-lg font-semibold text-lime-400">🌍 Select Country</label>
            <select
              id="country-select"
              className="w-full bg-slate-700 border border-slate-600 text-white py-2 px-4 rounded-lg focus:ring-2 focus:ring-lime-400"
              value={selectedCountryId}
              onChange={(e) => setSelectedCountryId(e.target.value)}
            >
              <option value="">-- Choose a Country --</option>
              {countries.map(country => (
                <option key={country.id} value={country.id}>{country.name}</option>
              ))}
            </select>
          </div>

          <div className="bg-slate-800 bg-opacity-50 rounded-xl p-6 shadow-lg backdrop-blur-md">
            <label htmlFor="team-select" className="block mb-2 text-lg font-semibold text-yellow-400">🗡 Select Team</label>
            <select
              id="team-select"
              className="w-full bg-slate-700 border border-slate-600 text-white py-2 px-4 rounded-lg focus:ring-2 focus:ring-yellow-400"
              value={selectedTeamId}
              onChange={(e) => setSelectedTeamId(e.target.value)}
              disabled={!selectedCountryId}
            >
              <option value="">-- Choose a Team --</option>
              {teams.map(team => (
                <option key={team.id} value={team.id}>{team.name}</option>
              ))}
            </select>
          </div>
        </div>

        {selectedTeamId && (
          <div className="bg-slate-800 bg-opacity-40 backdrop-blur-md rounded-xl p-6 shadow-xl border border-slate-700">
            <h2 className="text-3xl font-bold mb-6 text-center text-cyan-300">
              Squad: {teams.find(t => t.id === selectedTeamId)?.name}
            </h2>
            {players.length > 0 ? (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {players.map(player => (
                  <div
                    key={player.id}
                    className="bg-slate-700 p-5 rounded-xl shadow-lg hover:shadow-2xl hover:-translate-y-1 transform transition-all duration-300 border border-gray-600"
                  >
                    <p className="text-xl font-semibold text-white mb-1">{player.name}</p>
                    <p className="text-sm text-gray-300">Age: {player.age}</p>
                    <p className="text-sm text-gray-300">Nationality: {player.nationality}</p>
                    <p className="text-sm text-gray-300">Position: {player.position}</p>
                    <p className="text-sm text-gray-300">
                      Club-trained: <span className={player.clubTrained ? 'text-green-400' : 'text-red-400'}>{player.clubTrained ? 'Yes' : 'No'}</span>
                    </p>
                    <p className="text-sm text-gray-300">
                      Assoc-trained: <span className={player.associationTrained ? 'text-green-400' : 'text-red-400'}>{player.associationTrained ? 'Yes' : 'No'}</span>
                    </p>
                    <p className="text-sm text-gray-300">
                      U21 Player: <span className={player.isUnder21 ? 'text-yellow-300' : 'text-gray-400'}>{player.isUnder21 ? 'Yes' : 'No'}</span>
                    </p>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center text-gray-400 mt-4">No players found.</div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
