import React, { useState, useEffect } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const ItemTypes = {
  PLAYER: 'player',
};

const dummyLeagues = [
  { id: 'ucl', name: 'UEFA Champions League' },
  { id: 'uel', name: 'UEFA Europa League' },
  { id: 'ucl_conf', name: 'UEFA Conference League' },
];

const dummyTeams = [
  { id: 'fenerbahce', name: 'Fenerbahçe', leagueId: 'ucl' },
  { id: 'galatasaray', name: 'Galatasaray', leagueId: 'ucl' },
  { id: 'besiktas', name: 'Beşiktaş', leagueId: 'uel' },
  { id: 'samsunspor', name: 'Samsunspor', leagueId: 'uel' },
  { id: 'basaksehir', name: 'Başakşehir', leagueId: 'ucl_conf' },
];

// Dummy players örneği, her takım için 4'er oyuncu
const dummyPlayers = [
  // Fenerbahçe (UCL)
  { id: '1', name: 'Altay Bayındır', teamId: 'fenerbahce', isHomegrown: true },
  { id: '2', name: 'Ferdi Kadıoğlu', teamId: 'fenerbahce', isHomegrown: true },
  { id: '3', name: 'Mert Hakan Yandaş', teamId: 'fenerbahce', isHomegrown: false },
  { id: '4', name: 'İrfan Can Kahveci', teamId: 'fenerbahce', isHomegrown: false },

  // Galatasaray (UCL)
  { id: '5', name: 'Fernando Muslera', teamId: 'galatasaray', isHomegrown: false },
  { id: '6', name: 'Omar Elabdellaoui', teamId: 'galatasaray', isHomegrown: false },
  { id: '7', name: 'Kerem Aktürkoğlu', teamId: 'galatasaray', isHomegrown: true },
  { id: '8', name: 'Morutan', teamId: 'galatasaray', isHomegrown: false },

  // Beşiktaş (UEL)
  { id: '9', name: 'Ersin Destanoğlu', teamId: 'besiktas', isHomegrown: true },
  { id: '10', name: 'Josef de Souza', teamId: 'besiktas', isHomegrown: false },
  { id: '11', name: 'Rachid Ghezzal', teamId: 'besiktas', isHomegrown: false },
  { id: '12', name: 'Cenk Tosun', teamId: 'besiktas', isHomegrown: true },

  // Samsunspor (UEL)
  { id: '13', name: 'Mehmet Yılmaz', teamId: 'samsunspor', isHomegrown: true },
  { id: '14', name: 'Caner Kara', teamId: 'samsunspor', isHomegrown: true },
  { id: '15', name: 'Yusuf Demir', teamId: 'samsunspor', isHomegrown: false },
  { id: '16', name: 'Deniz Kaya', teamId: 'samsunspor', isHomegrown: false },

  // Başakşehir (Conference)
  { id: '17', name: 'Volkan Babacan', teamId: 'basaksehir', isHomegrown: true },
  { id: '18', name: 'Edin Višća', teamId: 'basaksehir', isHomegrown: false },
  { id: '19', name: 'İrfan Can Eğribayat', teamId: 'basaksehir', isHomegrown: true },
  { id: '20', name: 'Emre Belozoglu', teamId: 'basaksehir', isHomegrown: false },
];

// Player component (draggable)
function Player({ player }) {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PLAYER,
    item: { id: player.id },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  return (
    <div
      ref={drag}
      style={{
        opacity: isDragging ? 0.4 : 1,
        cursor: 'move',
        backgroundColor: player.isHomegrown ? '#4ade80' : '#60a5fa',
        color: 'white',
        padding: '8px 12px',
        margin: '4px',
        borderRadius: 6,
        fontWeight: 'bold',
        textAlign: 'center',
        minWidth: 120,
        userSelect: 'none',
      }}
      title={player.isHomegrown ? 'Homegrown Player' : 'Non-homegrown Player'}
    >
      {player.name}
    </div>
  );
}

// Drop area (pitch or bench)
function DropArea({ children, onDropPlayer, position }) {
  const [{ isOver }, drop] = useDrop({
    accept: ItemTypes.PLAYER,
    drop: (item) => {
      onDropPlayer(item.id, position);
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  });

  return (
    <div
      ref={drop}
      style={{
        backgroundColor: isOver ? '#1e293b' : '#0f172a',
        border: '2px dashed #334155',
        borderRadius: 10,
        padding: 16,
        minHeight: 200,
        flex: 1,
        overflowY: 'auto',
      }}
    >
      {children}
    </div>
  );
}

// Pitch component
function Pitch({ players, onDropPlayer }) {
  return (
    <DropArea onDropPlayer={onDropPlayer} position="pitch">
      <h2 style={{ color: '#fbbf24' }}>Pitch</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
        {players.map((p) => (
          <Player key={p.id} player={p} />
        ))}
      </div>
    </DropArea>
  );
}

// Bench component
function Bench({ players, onDropPlayer }) {
  return (
    <DropArea onDropPlayer={onDropPlayer} position="bench">
      <h2 style={{ color: '#94a3b8' }}>Bench</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
        {players.map((p) => (
          <Player key={p.id} player={p} />
        ))}
      </div>
    </DropArea>
  );
}

export default function App() {
  const [selectedLeague, setSelectedLeague] = useState('');
  const [selectedTeam, setSelectedTeam] = useState('');
  const [playerPositions, setPlayerPositions] = useState({}); // id -> 'pitch' | 'bench'

  // Filtrelenmiş takımlar & oyuncular
  const teams = dummyTeams.filter((team) => team.leagueId === selectedLeague);
  const players = dummyPlayers.filter((p) => p.teamId === selectedTeam);

  // Takım değişince oyuncular bench'e alınır
  useEffect(() => {
    if (selectedTeam) {
      const newPositions = {};
      players.forEach((p) => {
        newPositions[p.id] = 'bench';
      });
      setPlayerPositions(newPositions);
    }
  }, [selectedTeam]);

  // Pitch ve bench için oyuncu dizileri
  const pitchPlayers = players.filter((p) => playerPositions[p.id] === 'pitch');
  const benchPlayers = players.filter((p) => playerPositions[p.id] === 'bench');

  // UEFA kuralları (basitleştirilmiş)
  const squadSize = players.length;
  const homegrownCount = players.filter((p) => p.isHomegrown).length;
  const nonHomegrownCount = squadSize - homegrownCount;

  const isSquadValid =
    squadSize <= 25 &&
    homegrownCount >= 8 &&
    nonHomegrownCount <= 17;

  const onDropPlayer = (id, toPosition) => {
    setPlayerPositions((prev) => ({
      ...prev,
      [id]: toPosition,
    }));
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div style={{ backgroundColor: '#0f172a', color: '#fff', minHeight: '100vh', padding: 20, fontFamily: 'sans-serif' }}>
        <h1 style={{ color: '#60a5fa' }}>UEFA Squad Builder 2025/26</h1>

        <div style={{ display: 'flex', gap: 12, marginBottom: 20 }}>
          <select
            value={selectedLeague}
            onChange={(e) => {
              setSelectedLeague(e.target.value);
              setSelectedTeam('');
            }}
          >
            <option value="">-- Select League --</option>
            {dummyLeagues.map((l) => (
              <option key={l.id} value={l.id}>{l.name}</option>
            ))}
          </select>

          <select
            value={selectedTeam}
            onChange={(e) => setSelectedTeam(e.target.value)}
            disabled={!selectedLeague}
          >
            <option value="">-- Select Team --</option>
            {teams.map((t) => (
              <option key={t.id} value={t.id}>{t.name}</option>
            ))}
          </select>
        </div>

        {!selectedTeam && <p style={{ color: '#cbd5e1' }}>Please select a league and a team.</p>}

        {selectedTeam && (
          <>
            <div style={{ display: 'flex', gap: 20 }}>
              <Pitch players={pitchPlayers} onDropPlayer={onDropPlayer} />
              <Bench players={benchPlayers} onDropPlayer={onDropPlayer} />
            </div>

            <div style={{ maxWidth: 600, margin: '20px auto', color: '#cbd5e1' }}>
              <p>Squad size: <b>{squadSize}</b> (Max 25)</p>
              <p>Homegrown players: <b>{homegrownCount}</b> (Min 8)</p>
              <p>Non-homegrown players: <b>{nonHomegrownCount}</b> (Max 17)</p>

              {!isSquadValid ? (
                <p style={{ color: '#f87171', fontWeight: 'bold' }}>Squad does not meet UEFA requirements!</p>
              ) : (
                <p style={{ color: '#4ade80', fontWeight: 'bold' }}>Squad meets UEFA requirements!</p>
              )}
            </div>
          </>
        )}
      </div>
    </DndProvider>
  );
}
