// src/App.js
import React, { useState, useEffect, useCallback } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

// Dummy data for leagues, teams and players
const dummyData = {
  leagues: [
    { id: 'epl', name: 'English Premier League' },
    { id: 'la_liga', name: 'La Liga' },
    { id: 'super_lig', name: 'Turkish Super Lig' },
  ],
  teams: [
    { id: 'manchester_united', name: 'Manchester United', leagueId: 'epl' },
    { id: 'liverpool', name: 'Liverpool', leagueId: 'epl' },
    { id: 'real_madrid', name: 'Real Madrid', leagueId: 'la_liga' },
    { id: 'barcelona', name: 'Barcelona', leagueId: 'la_liga' },
    { id: 'fenerbahce', name: 'Fenerbahçe', leagueId: 'super_lig' },
    { id: 'galatasaray', name: 'Galatasaray', leagueId: 'super_lig' },
  ],
  players: [
    // Manchester United players
    { id: 'p1', name: 'David de Gea', teamId: 'manchester_united', position: 'GK', nationality: 'Spain', clubTrained: true, isUnder21: false },
    { id: 'p2', name: 'Harry Maguire', teamId: 'manchester_united', position: 'DEF', nationality: 'England', clubTrained: true, isUnder21: false },
    { id: 'p3', name: 'Bruno Fernandes', teamId: 'manchester_united', position: 'MID', nationality: 'Portugal', clubTrained: false, isUnder21: false },
    { id: 'p4', name: 'Marcus Rashford', teamId: 'manchester_united', position: 'FWD', nationality: 'England', clubTrained: true, isUnder21: true },
    // Fenerbahçe players
    { id: 'p5', name: 'Altay Bayındır', teamId: 'fenerbahce', position: 'GK', nationality: 'Turkey', clubTrained: true, isUnder21: false },
    { id: 'p6', name: 'Victor Nelsson', teamId: 'fenerbahce', position: 'DEF', nationality: 'Denmark', clubTrained: false, isUnder21: false },
    { id: 'p7', name: 'Arda Güler', teamId: 'fenerbahce', position: 'MID', nationality: 'Turkey', clubTrained: true, isUnder21: true },
    { id: 'p8', name: 'Edin Dzeko', teamId: 'fenerbahce', position: 'FWD', nationality: 'Bosnia', clubTrained: false, isUnder21: false },
  ],
};

// React DnD types
const ItemTypes = {
  PLAYER: 'player',
};

// Draggable Player Component
const PlayerCard = ({ player, movePlayer }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PLAYER,
    item: { id: player.id },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }), [player.id]);

  return (
    <div
      ref={drag}
      className={`bg-gray-800 p-2 rounded-lg mb-2 cursor-move text-white shadow-md ${
        isDragging ? 'opacity-50' : 'opacity-100'
      }`}
    >
      <strong>{player.name}</strong> ({player.position})<br />
      <small>{player.nationality}</small>
    </div>
  );
};

// Droppable Area for pitch or bench spots
const DropZone = ({ children, onDrop, label, maxPlayers, currentCount }) => {
  const [{ isOver, canDrop }, drop] = useDrop(() => ({
    accept: ItemTypes.PLAYER,
    drop: (item) => onDrop(item.id),
    canDrop: () => currentCount < maxPlayers,
    collect: (monitor) => ({
      isOver: !!monitor.isOver(),
      canDrop: !!monitor.canDrop(),
    }),
  }), [currentCount, maxPlayers]);

  return (
    <div
      ref={drop}
      className={`border-2 rounded-lg p-4 min-h-[100px] overflow-y-auto
      ${isOver && canDrop ? 'border-green-500 bg-green-900' : 'border-gray-700 bg-gray-800'}`}
    >
      <h3 className="text-lg mb-2 font-semibold text-white">{label} ({currentCount}/{maxPlayers})</h3>
      {children}
    </div>
  );
};

const App = () => {
  const [selectedLeague, setSelectedLeague] = useState('');
  const [teams, setTeams] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState('');
  const [availablePlayers, setAvailablePlayers] = useState([]);
  // Players assigned on pitch (max 11)
  const [pitchPlayers, setPitchPlayers] = useState([]);
  // Players assigned on bench (max 14)
  const [benchPlayers, setBenchPlayers] = useState([]);

  // Update teams when league changes
  useEffect(() => {
    if (!selectedLeague) {
      setTeams([]);
      setSelectedTeam('');
      setAvailablePlayers([]);
      setPitchPlayers([]);
      setBenchPlayers([]);
      return;
    }
    const filteredTeams = dummyData.teams.filter((t) => t.leagueId === selectedLeague);
    setTeams(filteredTeams);
    setSelectedTeam('');
    setAvailablePlayers([]);
    setPitchPlayers([]);
    setBenchPlayers([]);
  }, [selectedLeague]);

  // Update players when team changes
  useEffect(() => {
    if (!selectedTeam) {
      setAvailablePlayers([]);
      setPitchPlayers([]);
      setBenchPlayers([]);
      return;
    }
    const teamPlayers = dummyData.players.filter((p) => p.teamId === selectedTeam);
    setAvailablePlayers(teamPlayers);
    setPitchPlayers([]);
    setBenchPlayers([]);
  }, [selectedTeam]);

  // Move player between lists (available -> pitch/bench, pitch -> bench/available, bench -> pitch/available)
  const movePlayer = useCallback((playerId, to) => {
    // Find player object
    const allPlayers = [...availablePlayers, ...pitchPlayers, ...benchPlayers];
    const player = allPlayers.find((p) => p.id === playerId);
    if (!player) return;

    // Remove player from wherever it currently is
    setAvailablePlayers((aps) => aps.filter((p) => p.id !== playerId));
    setPitchPlayers((pps) => pps.filter((p) => p.id !== playerId));
    setBenchPlayers((bps) => bps.filter((p) => p.id !== playerId));

    // Add to destination
    if (to === 'pitch') {
      if (pitchPlayers.length >= 11) return alert('Pitch is full (max 11)');
      setPitchPlayers((pps) => [...pps, player]);
    } else if (to === 'bench') {
      if (benchPlayers.length >= 14) return alert('Bench is full (max 14)');
      setBenchPlayers((bps) => [...bps, player]);
    } else {
      setAvailablePlayers((aps) => [...aps, player]);
    }
  }, [pitchPlayers.length, benchPlayers.length, availablePlayers]);

  // Drop handlers
  const onDropToPitch = (playerId) => movePlayer(playerId, 'pitch');
  const onDropToBench = (playerId) => movePlayer(playerId, 'bench');
  const onDropToAvailable = (playerId) => movePlayer(playerId, 'available');

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="min-h-screen bg-gradient-to-r from-green-700 via-green-900 to-black p-6 text-white font-sans">
        <h1 className="text-4xl font-bold mb-8 text-center">UEFA Squad Builder</h1>

        {/* League Select */}
        <div className="mb-4 max-w-md mx-auto">
          <label htmlFor="league-select" className="block mb-2 font-semibold text-lg">Select League</label>
          <select
            id="league-select"
            value={selectedLeague}
            onChange={(e) => setSelectedLeague(e.target.value)}
            className="w-full p-2 rounded bg-green-800 border border-green-600"
          >
            <option value="">-- Choose League --</option>
            {dummyData.leagues.map((league) => (
              <option key={league.id} value={league.id}>{league.name}</option>
            ))}
          </select>
        </div>

        {/* Team Select */}
        {teams.length > 0 && (
          <div className="mb-8 max-w-md mx-auto">
            <label htmlFor="team-select" className="block mb-2 font-semibold text-lg">Select Team</label>
            <select
              id="team-select"
              value={selectedTeam}
              onChange={(e) => setSelectedTeam(e.target.value)}
              className="w-full p-2 rounded bg-green-800 border border-green-600"
            >
              <option value="">-- Choose Team --</option>
              {teams.map((team) => (
                <option key={team.id} value={team.id}>{team.name}</option>
              ))}
            </select>
          </div>
        )}

        {/* Main content */}
        {selectedTeam && (
          <div className="flex flex-col lg:flex-row gap-6 max-w-6xl mx-auto">
            {/* Left: Pitch + Bench */}
            <div className="flex flex-col gap-6 flex-1">
              <DropZone
                label="Pitch (max 11)"
                onDrop={onDropToPitch}
                maxPlayers={11}
                currentCount={pitchPlayers.length}
              >
                {pitchPlayers.map((player) => (
                  <PlayerCard
                    key={player.id}
                    player={player}
                    movePlayer={movePlayer}
                  />
                ))}
              </DropZone>

              <DropZone
                label="Bench (max 14)"
                onDrop={onDropToBench}
                maxPlayers={14}
                currentCount={benchPlayers.length}
              >
                {benchPlayers.map((player) => (
                  <PlayerCard
                    key={player.id}
                    player={player}
                    movePlayer={movePlayer}
                  />
                ))}
              </DropZone>
            </div>

            {/* Right: Available Players */}
            <DropZone
              label="Available Players"
              onDrop={onDropToAvailable}
              maxPlayers={99}
              currentCount={availablePlayers.length}
            >
              {availablePlayers.map((player) => (
                <PlayerCard
                  key={player.id}
                  player={player}
                  movePlayer={movePlayer}
                />
              ))}
            </DropZone>
          </div>
        )}
      </div>
    </DndProvider>
  );
};

export default App;
