// App.js
import React, { useState, useEffect } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import axios from 'axios';

const API_KEY = 'd7a2d54e3a08a4e15301963b6a0842a3';
const API_BASE = 'https://v3.football.api-sports.io';
const HEADERS = { 'x-apisports-key': API_KEY };
const ItemTypes = { PLAYER: 'player' };

function Player({ player }) {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PLAYER,
    item: { id: player.id },
    collect: monitor => ({ isDragging: monitor.isDragging() })
  }));

  return (
    <div
      ref={drag}
      style={{
        opacity: isDragging ? 0.4 : 1,
        cursor: 'move',
        backgroundColor: player.isHomegrown ? '#4ade80' : '#60a5fa',
        color: 'white',
        padding: '8px 12px',
        margin: '4px',
        borderRadius: 6,
        fontWeight: 'bold',
        textAlign: 'center',
        minWidth: 120
      }}
    >
      {player.name}
    </div>
  );
}

function DropArea({ children, onDropPlayer, position }) {
  const [{ isOver }, drop] = useDrop({
    accept: ItemTypes.PLAYER,
    drop: (item) => onDropPlayer(item.id, position),
    collect: monitor => ({ isOver: monitor.isOver() })
  });

  return (
    <div
      ref={drop}
      style={{
        backgroundColor: isOver ? '#1e293b' : '#0f172a',
        border: '2px dashed #334155',
        borderRadius: 10,
        padding: 16,
        minHeight: 200,
        flex: 1
      }}
    >
      {children}
    </div>
  );
}

function Pitch({ players, onDropPlayer }) {
  return (
    <DropArea onDropPlayer={onDropPlayer} position="pitch">
      <h2 style={{ color: '#fbbf24' }}>Pitch</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
        {players.map((p) => <Player key={p.id} player={p} />)}
      </div>
    </DropArea>
  );
}

function Bench({ players, onDropPlayer }) {
  return (
    <DropArea onDropPlayer={onDropPlayer} position="bench">
      <h2 style={{ color: '#94a3b8' }}>Bench</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
        {players.map((p) => <Player key={p.id} player={p} />)}
      </div>
    </DropArea>
  );
}

export default function App() {
  const [leagues, setLeagues] = useState([]);
  const [teams, setTeams] = useState([]);
  const [players, setPlayers] = useState([]);
  const [selectedLeague, setSelectedLeague] = useState('');
  const [selectedTeam, setSelectedTeam] = useState('');
  const [playerPositions, setPlayerPositions] = useState({});

  useEffect(() => {
    axios.get(`${API_BASE}/leagues?season=2023`, { headers: HEADERS })
      .then(res => setLeagues(res.data.response.map(l => ({
        id: l.league.id,
        name: l.league.name
      }))));
  }, []);

  useEffect(() => {
    if (selectedLeague) {
      axios.get(`${API_BASE}/teams?league=${selectedLeague}&season=2023`, { headers: HEADERS })
        .then(res => setTeams(res.data.response.map(t => ({
          id: t.team.id,
          name: t.team.name
        }))));
    }
  }, [selectedLeague]);

  useEffect(() => {
    if (selectedTeam) {
      axios.get(`${API_BASE}/players?team=${selectedTeam}&season=2023`, { headers: HEADERS })
        .then(res => {
          const flatPlayers = res.data.response.map(p => ({
            id: p.player.id.toString(),
            name: p.player.name,
            isHomegrown: false // placeholder
          }));
          setPlayers(flatPlayers);
          const initialPositions = {};
          flatPlayers.forEach(p => initialPositions[p.id] = 'bench');
          setPlayerPositions(initialPositions);
        });
    }
  }, [selectedTeam]);

  const pitchPlayers = players.filter(p => playerPositions[p.id] === 'pitch');
  const benchPlayers = players.filter(p => playerPositions[p.id] === 'bench');

  const onDropPlayer = (id, toPosition) => {
    setPlayerPositions(prev => ({ ...prev, [id]: toPosition }));
  };

  const squadSize = players.length;
  const homegrownCount = players.filter(p => p.isHomegrown).length;
  const nonHomegrownCount = squadSize - homegrownCount;
  const isSquadValid = squadSize <= 25 && homegrownCount >= 8 && nonHomegrownCount <= 17;

  return (
    <DndProvider backend={HTML5Backend}>
      <div style={{ backgroundColor: '#0f172a', color: '#fff', minHeight: '100vh', padding: 20 }}>
        <h1 style={{ color: '#60a5fa' }}>UEFA Squad Builder</h1>
        <div style={{ display: 'flex', gap: 12, marginBottom: 20 }}>
          <select value={selectedLeague} onChange={(e) => { setSelectedLeague(e.target.value); setSelectedTeam(''); }}>
            <option value="">-- Select League --</option>
            {leagues.map((l) => <option key={l.id} value={l.id}>{l.name}</option>)}
          </select>
          <select value={selectedTeam} onChange={(e) => setSelectedTeam(e.target.value)} disabled={!selectedLeague}>
            <option value="">-- Select Team --</option>
            {teams.map((t) => <option key={t.id} value={t.id}>{t.name}</option>)}
          </select>
        </div>

        {!selectedTeam && <p>Please select a league and a team.</p>}

        {selectedTeam && (
          <>
            <div style={{ display: 'flex', gap: 20 }}>
              <Pitch players={pitchPlayers} onDropPlayer={onDropPlayer} />
              <Bench players={benchPlayers} onDropPlayer={onDropPlayer} />
            </div>
            <div style={{ marginTop: 20 }}>
              <p>Squad size: <b>{squadSize}</b> (Max 25)</p>
              <p>Homegrown: <b>{homegrownCount}</b> (Min 8)</p>
              <p>Non-homegrown: <b>{nonHomegrownCount}</b> (Max 17)</p>
              <p style={{ fontWeight: 'bold', color: isSquadValid ? '#4ade80' : '#f87171' }}>
                {isSquadValid ? 'Squad meets UEFA requirements!' : 'Squad does not meet UEFA requirements!'}
              </p>
            </div>
          </>
        )}
      </div>
    </DndProvider>
  );
}
