import React, { useState, useEffect, useMemo } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

// --- Constants and Data ---
const ItemTypes = {
  PLAYER: 'player',
};

const TRAINING_STATUS = {
  CLUB: 'club-trained',
  ASSOC: 'association-trained',
  NONE: 'non-homegrown',
};

const POSITIONS = {
  GK: 'Goalkeeper',
  OUTFIELD: 'Outfield',
};

// --- Data with Detailed Player Info ---
// NOTE: Training status is an estimation for demonstration purposes.
const dummyLeagues = [
  { id: 'ucl', name: 'UEFA Champions League' },
  { id: 'uel', name: 'UEFA Europa League' },
  { id: 'uecl', name: 'UEFA Europa Conference League' },
];

const dummyTeams = [
  { id: 'fenerbahce', name: 'Fenerbahçe', leagueId: 'ucl' },
  { id: 'galatasaray', name: 'Galatasaray', leagueId: 'ucl' },
  { id: 'besiktas', name: 'Beşiktaş', leagueId: 'uel' },
];

const dummyPlayers = [
    // Fenerbahçe (UCL) - Updated from user
    { id: 'fb-livakovic', name: 'Dominik Livaković', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.GK },
    { id: 'fb-irfan-can-e', name: 'İrfan Can Eğribayat', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.GK },
    { id: 'fb-ertugrul-cetin', name: 'Ertugrul Cetin', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.GK },
    { id: 'fb-carlos', name: 'Diego Carlos', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-yusuf-akcicek', name: 'Yusuf Akcicek', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
    { id: 'fb-milan', name: 'Milan Skriniar', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-caglar', name: 'Çağlar Söyüncü', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'fb-djiku', name: 'Alexander Djiku', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-becao', name: 'Rodrigo Becão', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-oosterwolde', name: 'Jayden Oosterwolde', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-serdar-aziz', name: 'Serdar Aziz', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'fb-mert-muldur', name: 'Mert Müldür', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'fb-osayi-samuel', name: 'Bright Osayi-Samuel', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-oguz-aydin', name: 'Oguz Aydin', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'fb-ismail-yuksek', name: 'İsmail Yüksek', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
    { id: 'fb-sofyan', name: 'Amrabat', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-fred', name: 'Fred', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-szymanski', name: 'Sebastian Szymański', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-mert-hakan', name: 'Mert Hakan Yandaş', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'fb-cengiz', name: 'Cengiz Ünder', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'fb-irfan-can-k', name: 'İrfan Can Kahveci', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'fb-tadic', name: 'Dušan Tadić', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-saint-maximin', name: 'Allan Saint-Maximin', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-dzeko', name: 'Edin Džeko', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-cenk-tosun', name: 'Cenk Tosun', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'fb-en-nesyri', name: 'Youssef En-Nesyri', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-levent-mercan', name: 'Levent Mercan', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'fb-anderson', name: 'Talisca', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'fb-burak', name: 'Burak Kapacak', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },

    // Galatasaray (UCL)
    { id: 'gs-muslera', name: 'Fernando Muslera', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.GK },
    { id: 'gs-gunay', name: 'Günay Güvenç', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.GK },
    { id: 'gs-sanchez', name: 'Davinson Sánchez', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'gs-abdulkerim', name: 'Abdülkerim Bardakcı', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'gs-kaan-ayhan', name: 'Kaan Ayhan', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'gs-torreira', name: 'Lucas Torreira', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'gs-kerem-a', name: 'Kerem Aktürkoğlu', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
    { id: 'gs-icardi', name: 'Mauro Icardi', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'gs-yunus', name: 'Yunus Akgün', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
    { id: 'gs-emin', name: 'Emin Bayram', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
    { id: 'gs-baris-alper', name: 'Barış Alper Yılmaz', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },

    // Beşiktaş (UEL)
    { id: 'bjk-mert-gunok', name: 'Mert Günok', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.GK },
    { id: 'bjk-ersin', name: 'Ersin Destanoğlu', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.GK },
    { id: 'bjk-colley', name: 'Omar Colley', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'bjk-gedson', name: 'Gedson Fernandes', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
    { id: 'bjk-salih-ucan', name: 'Salih Uçan', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'bjk-semih', name: 'Semih Kılıçsoy', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
    { id: 'bjk-cenk-tosun', name: 'Cenk Tosun', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
    { id: 'bjk-necip', name: 'Necip Uysal', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
];

const UEFA_RULES = {
  MAX_SQUAD_SIZE: 25,
  MAX_NON_HOMEGROWN: 17,
  MIN_GOALKEEPERS: 2,
  MIN_TOTAL_HOMEGROWN: 8,
  MIN_CLUB_TRAINED: 4,
};

function Player({ player }) {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PLAYER,
    item: { id: player.id },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  const isHomegrown = player.trainingStatus === TRAINING_STATUS.CLUB || player.trainingStatus === TRAINING_STATUS.ASSOC;

  const tooltipText = {
    [TRAINING_STATUS.CLUB]: 'Club-Trained Player',
    [TRAINING_STATUS.ASSOC]: 'Association-Trained Player',
    [TRAINING_STATUS.NONE]: 'Non-Homegrown Player',
  };

  return (
    <div
      ref={drag}
      style={{
        opacity: isDragging ? 0.4 : 1,
        cursor: 'move',
        backgroundColor: isHomegrown ? '#4ade80' : '#60a5fa',
        color: 'white',
        padding: '8px 12px',
        margin: '4px',
        borderRadius: 6,
        fontWeight: 'bold',
        textAlign: 'center',
        minWidth: 120,
        userSelect: 'none',
      }}
      title={tooltipText[player.trainingStatus]}
    >
      {player.name}
      {player.position === POSITIONS.GK && ' (GK)'}
    </div>
  );
}

function DropArea({ children, onDropPlayer, position, title, titleColor }) {
  const [{ isOver }, drop] = useDrop({
    accept: ItemTypes.PLAYER,
    drop: (item) => {
      onDropPlayer(item.id, position);
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  });

  return (
    <div
      ref={drop}
      style={{
        backgroundColor: isOver ? '#1e293b' : '#0f172a',
        border: '2px dashed #334155',
        borderRadius: 10,
        padding: 16,
        minHeight: 200,
        flex: 1,
        overflowY: 'auto',
      }}
    >
      <h2 style={{ color: titleColor }}>{title}</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
        {children}
      </div>
    </div>
  );
}

function Pitch({ players, onDropPlayer }) {
  return (
    <DropArea onDropPlayer={onDropPlayer} position="pitch" title="Eligible Squad (List A)" titleColor="#fbbf24">
      {players.map((p) => <Player key={p.id} player={p} />)}
    </DropArea>
  );
}

function Bench({ players, onDropPlayer }) {
  return (
    <DropArea onDropPlayer={onDropPlayer} position="bench" title="Unregistered Players" titleColor="#94a3b8">
      {players.map((p) => <Player key={p.id} player={p} />)}
    </DropArea>
  );
}

function SquadValidationStatus({ pitchPlayers }) {
    const clubTrainedCount = pitchPlayers.filter(p => p.trainingStatus === TRAINING_STATUS.CLUB).length;
    const assocTrainedCount = pitchPlayers.filter(p => p.trainingStatus === TRAINING_STATUS.ASSOC).length;
    const nonHomegrownCount = pitchPlayers.filter(p => p.trainingStatus === TRAINING_STATUS.NONE).length;
    const totalHomegrownCount = clubTrainedCount + assocTrainedCount;
    const gkCount = pitchPlayers.filter(p => p.position === POSITIONS.GK).length;

    // The maximum number of players is 25, but it is reduced by the number of missing homegrown players.
    const homegrownDeficit = Math.max(0, UEFA_RULES.MIN_TOTAL_HOMEGROWN - totalHomegrownCount);
    const maxSquadSize = UEFA_RULES.MAX_SQUAD_SIZE - homegrownDeficit;

    // Primary validation checks
    const checks = {
        nonHomegrown: nonHomegrownCount <= UEFA_RULES.MAX_NON_HOMEGROWN,
        goalkeepers: gkCount >= UEFA_RULES.MIN_GOALKEEPERS,
        clubTrained: clubTrainedCount >= UEFA_RULES.MIN_CLUB_TRAINED,
        squadSize: pitchPlayers.length <= maxSquadSize,
    };

    const isSquadValid = Object.values(checks).every(Boolean);

    const renderCheck = (label, value, rule, isValid) => (
        <p style={{ color: isValid ? '#a7f3d0' : '#fca5a5' }}>
            {label}: <b>{value}</b> {rule} {isValid ? '✓' : '✗'}
        </p>
    );

    return (
        <div style={{ maxWidth: 600, margin: '20px auto', color: '#cbd5e1', padding: '10px', border: '1px solid #334155', borderRadius: '8px' }}>
            {renderCheck('Non-Homegrown Players', nonHomegrownCount, `(Max ${UEFA_RULES.MAX_NON_HOMEGROWN})`, checks.nonHomegrown)}
            {renderCheck('Goalkeepers', gkCount, `(Min ${UEFA_RULES.MIN_GOALKEEPERS})`, checks.goalkeepers)}
            {renderCheck('Club-Trained', clubTrainedCount, `(Min ${UEFA_RULES.MIN_CLUB_TRAINED})`, checks.clubTrained)}
            {renderCheck('Total Squad Size', pitchPlayers.length, `(Max ${maxSquadSize})`, checks.squadSize)}
             <p style={{ fontSize: '12px', color: '#94a3b8', marginTop: '5px' }}>
                Your squad size is capped at {maxSquadSize} because you have {totalHomegrownCount} of {UEFA_RULES.MIN_TOTAL_HOMEGROWN} required homegrown players.
            </p>
            <hr style={{ margin: '10px 0', borderColor: '#334155' }} />
            {isSquadValid ? (
                <p style={{ color: '#4ade80', fontWeight: 'bold' }}>Eligible squad meets UEFA requirements!</p>
            ) : (
                <p style={{ color: '#f87171', fontWeight: 'bold' }}>Eligible squad does not meet UEFA requirements!</p>
            )}
        </div>
    );
}

export default function App() {
  const [selectedLeague, setSelectedLeague] = useState('');
  const [selectedTeam, setSelectedTeam] = useState('');
  const [playerPositions, setPlayerPositions] = useState({});
  const [errorMessage, setErrorMessage] = useState('');

  const availableTeams = useMemo(() => dummyTeams.filter(t => t.leagueId === selectedLeague), [selectedLeague]);
  const allPlayersForTeam = useMemo(() => dummyPlayers.filter(p => p.teamId === selectedTeam), [selectedTeam]);

  useEffect(() => {
    if (selectedTeam) {
      const initialPositions = allPlayersForTeam.reduce((acc, player) => {
        acc[player.id] = 'bench';
        return acc;
      }, {});
      setPlayerPositions(initialPositions);
      setErrorMessage(''); 
    } else {
      setPlayerPositions({});
    }
  }, [selectedTeam, allPlayersForTeam]);

  useEffect(() => {
    if (errorMessage) {
      const timer = setTimeout(() => setErrorMessage(''), 3000);
      return () => clearTimeout(timer);
    }
  }, [errorMessage]);


  const pitchPlayers = useMemo(() => allPlayersForTeam.filter(p => playerPositions[p.id] === 'pitch'), [allPlayersForTeam, playerPositions]);
  const benchPlayers = useMemo(() => allPlayersForTeam.filter(p => playerPositions[p.id] === 'bench'), [allPlayersForTeam, playerPositions]);

  const calculateMaxSquadSize = (currentPitchPlayers) => {
    const totalHomegrownCount = currentPitchPlayers.filter(p => p.trainingStatus === TRAINING_STATUS.CLUB || p.trainingStatus === TRAINING_STATUS.ASSOC).length;
    const homegrownDeficit = Math.max(0, UEFA_RULES.MIN_TOTAL_HOMEGROWN - totalHomegrownCount);
    return UEFA_RULES.MAX_SQUAD_SIZE - homegrownDeficit;
  };

  const onDropPlayer = (playerId, toPosition) => {
    setErrorMessage(''); // Clear previous errors on a new action
    const playerToAdd = allPlayersForTeam.find(p => p.id === playerId);

    if (toPosition === 'pitch' && playerPositions[playerId] !== 'pitch') {
      // Check non-homegrown limit
      if(playerToAdd.trainingStatus === TRAINING_STATUS.NONE) {
        const nonHomegrownCount = pitchPlayers.filter(p => p.trainingStatus === TRAINING_STATUS.NONE).length;
        if (nonHomegrownCount >= UEFA_RULES.MAX_NON_HOMEGROWN) {
          setErrorMessage(`Cannot add more than ${UEFA_RULES.MAX_NON_HOMEGROWN} non-homegrown players.`);
          return;
        }
      }

      // Check total squad size limit
      const maxSquadSize = calculateMaxSquadSize(pitchPlayers);
      if (pitchPlayers.length >= maxSquadSize) {
        setErrorMessage(`Maximum squad size of ${maxSquadSize} reached.`);
        return;
      }
    }
    setPlayerPositions(prev => ({ ...prev, [playerId]: toPosition }));
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div style={{ backgroundColor: '#0f172a', color: '#fff', minHeight: '100vh', padding: 20, fontFamily: 'sans-serif' }}>
        <h1 style={{ color: '#60a5fa', textAlign: 'center' }}>UEFA Squad Builder (Detailed Rules)</h1>

        <div style={{ display: 'flex', justifyContent: 'center', gap: 12, marginBottom: 20 }}>
          <select
            value={selectedLeague}
            onChange={(e) => {
              setSelectedLeague(e.target.value);
              setSelectedTeam('');
            }}
            style={{ padding: '8px', borderRadius: '4px', border: '1px solid #334155', backgroundColor: '#1e293b', color: '#fff' }}
          >
            <option value="">-- Select League --</option>
            {dummyLeagues.map((l) => <option key={l.id} value={l.id}>{l.name}</option>)}
          </select>

          <select
            value={selectedTeam}
            onChange={(e) => setSelectedTeam(e.target.value)}
            disabled={!selectedLeague}
            style={{ padding: '8px', borderRadius: '4px', border: '1px solid #334155', backgroundColor: '#1e293b', color: '#fff' }}
          >
            <option value="">-- Select Team --</option>
            {availableTeams.map((t) => <option key={t.id} value={t.id}>{t.name}</option>)}
          </select>
        </div>

        {!selectedTeam && <p style={{ color: '#cbd5e1', textAlign: 'center' }}>Please select a league and a team.</p>}

        {selectedTeam && (
          <>
            <div style={{ display: 'flex', gap: 20 }}>
              <Pitch players={pitchPlayers} onDropPlayer={onDropPlayer} />
              <Bench players={benchPlayers} onDropPlayer={onDropPlayer} />
            </div>

            {errorMessage && <p style={{ color: '#f87171', fontWeight: 'bold', textAlign: 'center', marginTop: '10px' }}>{errorMessage}</p>}

            <SquadValidationStatus pitchPlayers={pitchPlayers} />
          </>
        )}
      </div>
    </DndProvider>
  );
}
