  { id: 'sam-satka', name: 'Ľubomír Šatka', teamId: 'samsunspor', isHomegrown: false },
  { id: 'sam-zeki', name: 'Zeki Yavru', teamId: 'samsunspor', isHomegrown: true },
  { id: 'sam-bola', name: 'Marc Bola', teamId: 'samsunspor', isHomegrown: false },
  { id: 'sam-ait-bennasser', name: 'Youssef Aït Bennasser', teamId: 'samsunspor', isHomegrown: false },
  { id: 'sam-tait', name: 'Flavien Tait', teamId: 'samsunspor', isHomegrown: false },
