  { id: 'sam-mouandilmadji', name: 'Marius Mouandilmadji', teamId: 'samsunspor', isHomegrown: false },
];


const UEFA_RULES = {
  MAX_SQUAD_SIZE: 25,
  MIN_HOMEGROWN: 8,
  MAX_NON_HOMEGROWN: 17,
};

// --- Reusable Components ---

function Player({ player }) {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PLAYER,
    item: { id: player.id, isHomegrown: player.isHomegrown },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  const playerBgClass = player.isHomegrown ? 'bg-green-600 hover:bg-green-500' : 'bg-blue-600 hover:bg-blue-500';
  const tooltipText = player.isHomegrown ? 'Homegrown Player' : 'Non-Homegrown Player';

  return (
    <div
      ref={drag}
      className={`p-2 px-3 rounded-md font-bold text-white shadow-lg transition-all duration-200 cursor-move select-none ${playerBgClass} ${isDragging ? 'opacity-40 scale-105' : 'opacity-100'}`}
      title={tooltipText}
    >
      {player.name}
    </div>
  );
}

function DropZone({ children, onDropPlayer, position, title, titleColor, canDrop }) {
  const [{ isOver, isDropAllowed }, drop] = useDrop({
    accept: ItemTypes.PLAYER,
    drop: (item) => onDropPlayer(item.id, position),
    canDrop: (item) => canDrop(item, position),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      isDropAllowed: monitor.canDrop(),
    }),
  });

  const baseBorder = "border-slate-600";
  const hoverBorder = isDropAllowed ? "border-emerald-500" : "border-red-500";
  const borderColor = isOver ? hoverBorder : baseBorder;

  return (
    <div ref={drop} className={`bg-slate-800/50 border-2 border-dashed rounded-lg p-4 flex-1 transition-colors duration-300 ${borderColor}`}>
      <h2 className={`text-xl font-semibold mb-4 ${titleColor}`}>{title}</h2>
      <div className="flex flex-wrap gap-2 min-h-[200px] content-start">
        {children}
      </div>
    </div>
  );
}

function SquadStats({ pitchPlayers }) {
  const pitchSquadSize = pitchPlayers.length;
  const homegrownCount = pitchPlayers.filter(p => p.isHomegrown).length;
  const nonHomegrownCount = pitchSquadSize - homegrownCount;

  const validationChecks = [
    {
      label: 'Eligible Squad Size',
      value: `${pitchSquadSize} / ${UEFA_RULES.MAX_SQUAD_SIZE}`,
      isValid: pitchSquadSize <= UEFA_RULES.MAX_SQUAD_SIZE,
      ruleText: `(Max ${UEFA_RULES.MAX_SQUAD_SIZE})`,
    },
    {
      label: 'Homegrown Players',
      value: `${homegrownCount} / ${UEFA_RULES.MIN_HOMEGROWN}`,
      isValid: homegrownCount >= UEFA_RULES.MIN_HOMEGROWN,
      ruleText: `(Min ${UEFA_RULES.MIN_HOMEGROWN})`,
    },
    {
      label: 'Non-Homegrown Players',
      value: `${nonHomegrownCount} / ${UEFA_RULES.MAX_NON_HOMEGROWN}`,
      isValid: nonHomegrownCount <= UEFA_RULES.MAX_NON_HOMEGROWN,
      ruleText: `(Max ${UEFA_RULES.MAX_NON_HOMEGROWN})`,
    },
  ];

  const isSquadFullyValid = validationChecks.every(check => check.isValid);

  return (
    <div className="mt-6 p-4 bg-slate-800 rounded-lg max-w-4xl mx-auto text-slate-300 shadow-xl">
      <h3 className="text-lg font-bold text-white mb-4">UEFA Squad Rules Validation</h3>
      <div className="space-y-2">
        {validationChecks.map(check => (
          <div key={check.label} className="flex justify-between items-center p-2 bg-slate-700/50 rounded-md">
            <div className="flex items-center">
              {check.isValid ? (
                <CheckCircle className="text-green-500 mr-3" size={20} />
              ) : (
                <XCircle className="text-red-500 mr-3" size={20} />
              )}
              <span className="font-semibold text-white">{check.label}</span>
              <span className="text-sm text-slate-400 ml-2">{check.ruleText}</span>
            </div>
            <span className={`font-bold text-lg ${check.isValid ? 'text-green-400' : 'text-red-400'}`}>
              {check.value}
            </span>
          </div>
        ))}
      </div>
       <div className={`mt-4 p-3 rounded-lg font-bold text-center text-lg transition-colors ${isSquadFullyValid ? 'bg-green-500/20 text-green-400' : 'bg-red-500/20 text-red-400'}`}>
        {isSquadFullyValid
          ? 'This squad meets all UEFA requirements!'
          : 'This squad does not meet UEFA requirements!'}
      </div>
    </div>
  );
}

function Notification({ message, onDismiss }) {
  if (!message) return null;

  useEffect(() => {
    const timer = setTimeout(onDismiss, 3000);
    return () => clearTimeout(timer);
  }, [message, onDismiss]);

  return (
    <div className="fixed top-5 right-5 bg-red-600 text-white py-2 px-4 rounded-lg shadow-lg flex items-center animate-pulse">
      <AlertTriangle className="mr-2" />
      {message}
    </div>
  );
}

// --- Main App Component ---

export default function App() {
  const [selectedLeague, setSelectedLeague] = useState('');
  const [selectedTeam, setSelectedTeam] = useState('');
  const [playerPositions, setPlayerPositions] = useState({});
  const [notification, setNotification] = useState(null);

  const availableTeams = useMemo(() => dummyTeams.filter(t => t.leagueId === selectedLeague), [selectedLeague]);
  const allPlayersForTeam = useMemo(() => dummyPlayers.filter(p => p.teamId === selectedTeam), [selectedTeam]);

  useEffect(() => {
    if (selectedTeam) {
      const initialPositions = allPlayersForTeam.reduce((acc, player) => {
        acc[player.id] = 'bench';
        return acc;
      }, {});
      setPlayerPositions(initialPositions);
    } else {
      setPlayerPositions({});
    }
  }, [selectedTeam, allPlayersForTeam]);

  const pitchPlayers = useMemo(() => allPlayersForTeam.filter(p => playerPositions[p.id] === 'pitch'), [allPlayersForTeam, playerPositions]);
  const benchPlayers = useMemo(() => allPlayersForTeam.filter(p => playerPositions[p.id] === 'bench'), [allPlayersForTeam, playerPositions]);

  const handleDropPlayer = (playerId, toPosition) => {
    setPlayerPositions(prev => ({
      ...prev,
      [playerId]: toPosition,
    }));
  };

  const canDropToPitch = (item, position) => {
    if (position !== 'pitch') return true; // always allow drop to bench
    // check if player is already on pitch
    if(playerPositions[item.id] === 'pitch') return true;

    if (pitchPlayers.length >= UEFA_RULES.MAX_SQUAD_SIZE) {
        return false;
    }
    return true;
  }

    const onDropPlayer = (id, toPosition) => {
        if (toPosition === 'pitch' && pitchPlayers.length >= 25 && playerPositions[id] !== 'pitch') {
          setNotification(`Maximum ${UEFA_RULES.MAX_SQUAD_SIZE} players allowed on the pitch.`);
          return;
        }
        handleDropPlayer(id, toPosition);
    };


  return (
    <DndProvider backend={HTML5Backend}>
      <div className="bg-slate-900 text-white min-h-screen p-4 md:p-8 font-sans">
        <Notification message={notification} onDismiss={() => setNotification(null)} />
        <header className="text-center mb-8">
          <h1 className="text-4xl font-extrabold text-blue-400 tracking-wider">UEFA Squad Builder</h1>
          <p className="text-slate-400 text-lg">2025/2026 Season Rules</p>
        </header>

        <div className="flex flex-col sm:flex-row justify-center items-center gap-4 mb-8 p-4 bg-slate-800/50 rounded-lg max-w-xl mx-auto">
          <select
            value={selectedLeague}
            onChange={(e) => {
              setSelectedLeague(e.target.value);
              setSelectedTeam('');
            }}
            className="bg-slate-700 border border-slate-600 rounded-md p-2 w-full sm:w-auto focus:ring-2 focus:ring-blue-500 focus:outline-none"
          >
            <option value="">-- Select League --</option>
            {dummyLeagues.map(l => <option key={l.id} value={l.id}>{l.name}</option>)}
          </select>

          <select
            value={selectedTeam}
            onChange={(e) => setSelectedTeam(e.target.value)}
            disabled={!selectedLeague}
            className="bg-slate-700 border border-slate-600 rounded-md p-2 w-full sm:w-auto disabled:opacity-50 disabled:cursor-not-allowed focus:ring-2 focus:ring-blue-500 focus:outline-none"
          >
            <option value="">-- Select Team --</option>
            {availableTeams.map(t => <option key={t.id} value={t.id}>{t.name}</option>)}
          </select>
        </div>

        {selectedTeam ? (
          <>
            <div className="flex flex-col lg:flex-row gap-8">
              <DropZone
                onDropPlayer={onDropPlayer}
                position="pitch"
                title="Eligible Squad (Pitch)"
                titleColor="text-amber-400"
                canDrop={canDropToPitch}
              >
                {pitchPlayers.map(p => <Player key={p.id} player={p} />)}
              </DropZone>

              <DropZone
                onDropPlayer={onDropPlayer}
                position="bench"
                title="Full Squad (Bench)"
                titleColor="text-slate-400"
                canDrop={() => true}
              >
                {benchPlayers.map(p => <Player key={p.id} player={p} />)}
              </DropZone>
            </div>
            <SquadStats pitchPlayers={pitchPlayers} />
          </>
        ) : (
          <div className="text-center py-16 px-4 bg-slate-800/50 rounded-lg max-w-xl mx-auto">
            <h2 className="text-2xl font-bold text-slate-300">Welcome!</h2>
            <p className="text-slate-400 mt-2">Please select a league and a team to start building your squad.</p>
          </div>
        )}
      </div>
    </DndProvider>
  );
}
