import React, { useState, useEffect } from 'react';

const App = () => {
  const initialData = {
    countries: [
      { id: 'turkey', name: '🇹🇷 Turkey' },
      { id: 'england', name: '🏴 England' },
      { id: 'spain', name: '🇪🇸 Spain' },
    ],
    teams: [
      { id: 'fenerbahce', name: '🟡 Fenerbahçe', countryId: 'turkey' },
      { id: 'galatasaray', name: '🔴 Galatasaray', countryId: 'turkey' },
      { id: 'besiktas', name: '⚪ Beşiktaş', countryId: 'turkey' },
      { id: 'manchester_united', name: '🔴 Manchester United', countryId: 'england' },
      { id: 'real_madrid', name: '⚪ Real Madrid', countryId: 'spain' },
    ],
    players: [
      { id: 'player1', name: 'Altay Bayındır', teamId: 'manchester_united', age: 26, nationality: '🇹🇷 Turkish', clubTrained: true, associationTrained: true, position: '🧤 Goalkeeper', isUnder21: false },
      { id: 'player2', name: 'Ferdi Kadıoğlu', teamId: 'fenerbahce', age: 24, nationality: '🇹🇷 Turkish', clubTrained: true, associationTrained: true, position: '🛡 Defender', isUnder21: false },
      { id: 'player3', name: 'Mert Hakan Yandaş', teamId: 'fenerbahce', age: 29, nationality: '🇹🇷 Turkish', clubTrained: false, associationTrained: true, position: '⚙️ Midfielder', isUnder21: false },
      { id: 'player4', name: 'Arda Güler', teamId: 'real_madrid', age: 19, nationality: '🇹🇷 Turkish', clubTrained: true, associationTrained: true, position: '⚙️ Midfielder', isUnder21: true },
      { id: 'player5', name: 'Cengiz Ünder', teamId: 'fenerbahce', age: 26, nationality: '🇹🇷 Turkish', clubTrained: false, associationTrained: false, position: '🎯 Forward', isUnder21: false },
      { id: 'player6', name: 'Fred', teamId: 'fenerbahce', age: 31, nationality: '🇧🇷 Brazilian', clubTrained: false, associationTrained: false, position: '⚙️ Midfielder', isUnder21: false },
      { id: 'player7', name: 'Edin Dzeko', teamId: 'fenerbahce', age: 38, nationality: '🇧🇦 Bosnian', clubTrained: false, associationTrained: false, position: '🎯 Forward', isUnder21: false },
      { id: 'player8', name: 'Victor Nelsson', teamId: 'galatasaray', age: 25, nationality: '🇩🇰 Danish', clubTrained: false, associationTrained: false, position: '🛡 Defender', isUnder21: false },
      { id: 'player9', name: 'Mauro Icardi', teamId: 'galatasaray', age: 31, nationality: '🇦🇷 Argentine', clubTrained: false, associationTrained: false, position: '🎯 Forward', isUnder21: false },
    ],
  };

  const [countries] = useState(initialData.countries);
  const [teams, setTeams] = useState([]);
  const [players, setPlayers] = useState([]);
  const [selectedCountryId, setSelectedCountryId] = useState('');
  const [selectedTeamId, setSelectedTeamId] = useState('');

  useEffect(() => {
    if (selectedCountryId) {
      const filteredTeams = initialData.teams.filter(team => team.countryId === selectedCountryId);
      setTeams(filteredTeams);
    } else {
      setTeams([]);
    }
    setSelectedTeamId('');
    setPlayers([]);
  }, [selectedCountryId]);

  useEffect(() => {
    if (selectedTeamId) {
      const filteredPlayers = initialData.players.filter(player => player.teamId === selectedTeamId);
      setPlayers(filteredPlayers);
    } else {
      setPlayers([]);
    }
  }, [selectedTeamId]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#1e1e2f] via-[#111827] to-black text-gray-100 font-sans p-4 sm:p-8 relative overflow-hidden">
      {/* Animated background bubbles */}
      <div className="absolute top-0 left-0 w-full h-full overflow-hidden z-0">
        <div className="absolute bg-pink-500 opacity-20 w-96 h-96 rounded-full blur-3xl top-10 left-10 animate-pulse"></div>
        <div className="absolute bg-blue-500 opacity-20 w-80 h-80 rounded-full blur-3xl bottom-10 right-10 animate-ping"></div>
      </div>

      <div className="relative z-10 max-w-6xl mx-auto">
        <h1 className="text-5xl font-extrabold text-center mb-12 text-transparent bg-clip-text bg-gradient-to-r from-pink-500 to-purple-500 drop-shadow-lg">
          ✨ CL Squad Builder
        </h1>

        <div className="grid md:grid-cols-2 gap-10 mb-12">
          {/* Country Selection */}
          <div className="backdrop-blur bg-white/5 p-6 rounded-2xl shadow-lg border border-pink-500/30">
            <label className="text-lg font-semibold mb-2 block">Select Country</label>
            <select
              value={selectedCountryId}
              onChange={e => setSelectedCountryId(e.target.value)}
              className="w-full p-3 rounded-lg bg-black/50 border border-gray-600 text-white focus:ring-2 focus:ring-pink-500"
            >
              <option value="">-- Choose a Country --</option>
              {countries.map(country => (
                <option key={country.id} value={country.id}>{country.name}</option>
              ))}
            </select>
          </div>

          {/* Team Selection */}
          <div className="backdrop-blur bg-white/5 p-6 rounded-2xl shadow-lg border border-purple-500/30">
            <label className="text-lg font-semibold mb-2 block">Select Team</label>
            <select
              value={selectedTeamId}
              onChange={e => setSelectedTeamId(e.target.value)}
              disabled={!selectedCountryId || teams.length === 0}
              className="w-full p-3 rounded-lg bg-black/50 border border-gray-600 text-white focus:ring-2 focus:ring-purple-500 disabled:opacity-40"
            >
              <option value="">-- Choose a Team --</option>
              {teams.map(team => (
                <option key={team.id} value={team.id}>{team.name}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Players */}
        {selectedTeamId && (
          <div className="relative z-10">
            <h2 className="text-3xl font-bold text-pink-400 mb-6">
              🧑‍🎓 Players in {teams.find(t => t.id === selectedTeamId)?.name}
            </h2>
            <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {players.map(player => (
                <div
                  key={player.id}
                  className="bg-white/10 border border-white/20 backdrop-blur-md rounded-2xl p-5 transition-transform hover:scale-105 hover:shadow-xl"
                >
                  <h3 className="text-xl font-bold mb-2 text-white">{player.name}</h3>
                  <p className="text-sm text-gray-300">{player.position}</p>
                  <p className="text-sm text-gray-400">🌍 {player.nationality}</p>
                  <p className="text-sm text-gray-400">🎂 Age: {player.age}</p>
                  <p className="text-sm">
                    🏫 Club-trained:
                    <span className={player.clubTrained ? 'text-green-400 font-semibold ml-1' : 'text-red-400 ml-1'}>
                      {player.clubTrained ? 'Yes' : 'No'}
                    </span>
                  </p>
                  <p className="text-sm">
                    🏢 Assoc-trained:
                    <span className={player.associationTrained ? 'text-green-400 font-semibold ml-1' : 'text-red-400 ml-1'}>
                      {player.associationTrained ? 'Yes' : 'No'}
                    </span>
                  </p>
                  <p className="text-sm">
                    🧒 U21:
                    <span className={player.isUnder21 ? 'text-yellow-400 font-semibold ml-1' : 'text-gray-400 ml-1'}>
                      {player.isUnder21 ? 'Yes' : 'No'}
                    </span>
                  </p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
