import React, { useState, useEffect } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const ItemTypes = {
  PLAYER: 'player',
};

function Player({ player }) {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PLAYER,
    item: { id: player.id },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }), [player]);

  return (
    <div
      ref={drag}
      className={`cursor-move rounded-md p-2 border border-gray-400 bg-gray-800 shadow-md select-none text-white w-32 text-center
      ${isDragging ? 'opacity-50' : 'opacity-100'}
      `}
      title={`${player.name} - ${player.position}`}
    >
      <p className="font-semibold truncate">{player.name}</p>
      <p className="text-xs text-gray-300">{player.position}</p>
    </div>
  );
}

function Pitch({ pitchPlayers, onDropPlayer }) {
  const [{ isOver, canDrop }, drop] = useDrop(() => ({
    accept: ItemTypes.PLAYER,
    drop: (item) => onDropPlayer(item.id, 'pitch'),
    canDrop: (item) => {
      // Allow drop if player already on pitch or if pitch has less than 11 players
      return pitchPlayers.length < 11 || pitchPlayers.some(p => p.id === item.id);
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  }), [pitchPlayers, onDropPlayer]);

  const bgColor = isOver ? (canDrop ? 'bg-green-600' : 'bg-red-600') : 'bg-gray-900';

  return (
    <div
      ref={drop}
      className={`${bgColor} rounded-lg p-4 flex flex-wrap gap-3 min-h-[300px] flex-grow max-w-[700px]`}
      style={{ border: '2px dashed #666' }}
    >
      <h3 className="w-full text-yellow-400 font-bold mb-2">Football Pitch (max 11 players)</h3>
      {pitchPlayers.length === 0 && (
        <p className="text-gray-500 italic w-full text-center">Drag players here to start building your lineup</p>
      )}
      {pitchPlayers.map(player => (
        <Player key={player.id} player={player} />
      ))}
    </div>
  );
}

function Bench({ benchPlayers, onDropPlayer }) {
  const [{ isOver, canDrop }, drop] = useDrop(() => ({
    accept: ItemTypes.PLAYER,
    drop: (item) => onDropPlayer(item.id, 'bench'),
    canDrop: () => true,
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  }), [onDropPlayer]);

  const bgColor = isOver ? (canDrop ? 'bg-green-600' : 'bg-red-600') : 'bg-gray-800';

  return (
    <div
      ref={drop}
      className={`${bgColor} rounded-lg p-4 flex flex-wrap gap-3 min-h-[300px] flex-grow max-w-[350px]`}
      style={{ border: '2px dashed #666' }}
    >
      <h3 className="w-full text-yellow-400 font-bold mb-2">Bench</h3>
      {benchPlayers.length === 0 ? (
        <p className="text-gray-500 italic w-full text-center">No players on bench</p>
      ) : (
        benchPlayers.map(player => <Player key={player.id} player={player} />)
      )}
    </div>
  );
}

const initialData = {
  leagues: [
    { id: 'ucl', name: 'UEFA Champions League' },
    { id: 'uel', name: 'UEFA Europa League' },
  ],
  teams: [
    { id: 'fenerbahce', name: 'Fenerbahçe', leagueId: 'ucl' },
    { id: 'galatasaray', name: 'Galatasaray', leagueId: 'ucl' },
    { id: 'besiktas', name: 'Beşiktaş', leagueId: 'uel' },
    { id: 'manchester_united', name: 'Manchester United', leagueId: 'ucl' },
    { id: 'real_madrid', name: 'Real Madrid', leagueId: 'uel' },
  ],
  players: [
    { id: 'player1', name: 'Altay Bayındır', teamId: 'fenerbahce', position: 'Goalkeeper' },
    { id: 'player2', name: 'Ferdi Kadıoğlu', teamId: 'fenerbahce', position: 'Defender' },
    { id: 'player3', name: 'Mert Hakan Yandaş', teamId: 'fenerbahce', position: 'Midfielder' },
    { id: 'player4', name: 'Arda Güler', teamId: 'real_madrid', position: 'Midfielder' },
    { id: 'player5', name: 'Cengiz Ünder', teamId: 'fenerbahce', position: 'Forward' },
    { id: 'player6', name: 'Fred', teamId: 'fenerbahce', position: 'Midfielder' },
    { id: 'player7', name: 'Edin Dzeko', teamId: 'fenerbahce', position: 'Forward' },
    { id: 'player8', name: 'Victor Nelsson', teamId: 'galatasaray', position: 'Defender' },
    { id: 'player9', name: 'Mauro Icardi', teamId: 'galatasaray', position: 'Forward' },
    { id: 'player10', name: 'Cristiano Ronaldo', teamId: 'manchester_united', position: 'Forward' },
    { id: 'player11', name: 'Marcus Rashford', teamId: 'manchester_united', position: 'Forward' },
    { id: 'player12', name: 'David De Gea', teamId: 'manchester_united', position: 'Goalkeeper' },
    { id: 'player13', name: 'Sergio Ramos', teamId: 'real_madrid', position: 'Defender' },
  ],
};

function App() {
  const [selectedLeagueId, setSelectedLeagueId] = useState('');
  const [selectedTeamId, setSelectedTeamId] = useState('');
  const [availableTeams, setAvailableTeams] = useState([]);
  const [teamPlayers, setTeamPlayers] = useState([]);

  // State for pitch and bench assignments
  // pitchPlayers and benchPlayers store player objects
  const [pitchPlayers, setPitchPlayers] = useState([]);
  const [benchPlayers, setBenchPlayers] = useState([]);

  // Update available teams when league changes
  useEffect(() => {
    if (selectedLeagueId) {
      const filteredTeams = initialData.teams.filter(team => team.leagueId === selectedLeagueId);
      setAvailableTeams(filteredTeams);
    } else {
      setAvailableTeams([]);
    }
    setSelectedTeamId('');
    setTeamPlayers([]);
    setPitchPlayers([]);
    setBenchPlayers([]);
  }, [selectedLeagueId]);

  // Update players when team changes
  useEffect(() => {
    if (selectedTeamId) {
      const players = initialData.players.filter(player => player.teamId === selectedTeamId);
      setTeamPlayers(players);
      setPitchPlayers([]);
      setBenchPlayers(players); // All players start on bench
    } else {
      setTeamPlayers([]);
      setPitchPlayers([]);
      setBenchPlayers([]);
    }
  }, [selectedTeamId]);

  // Handle drop of player to either pitch or bench
  const handleDropPlayer = (playerId, destination) => {
    const player = teamPlayers.find(p => p.id === playerId);
    if (!player) return;

    if (destination === 'pitch') {
      // If player already on pitch, do nothing
      if (pitchPlayers.some(p => p.id === playerId)) return;
      if (pitchPlayers.length >= 11) {
        alert('Pitch is full (max 11 players).');
        return;
      }
      setPitchPlayers([...pitchPlayers, player]);
      setBenchPlayers(benchPlayers.filter(p => p.id !== playerId));
    } else if (destination === 'bench') {
      // If player already on bench, do nothing
      if (benchPlayers.some(p => p.id === playerId)) return;
      setBenchPlayers([...benchPlayers, player]);
      setPitchPlayers(pitchPlayers.filter(p => p.id !== playerId));
    }
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="min-h-screen bg-gradient-to-br from-green-900 via-black to-green-900 text-white font-sans p-6 flex flex-col items-center">
        <h1 className="text-5xl font-extrabold mb-6 bg-clip-text text-transparent bg-gradient-to-r from-yellow-400 via-red-500 to-pink-500 select-none">
          UEFA Squad Builder
        </h1>

        {/* League & Team Selection */}
        <div className="w-full max-w-5xl mb-8 grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block mb-2 font-semibold text-yellow-300">Select League:</label>
            <select
              value={selectedLeagueId}
              onChange={e => setSelectedLeagueId(e.target.value)}
              className="w-full bg-gray-800 text-white p-3 rounded-lg shadow-inner focus:outline-none focus:ring-2 focus:ring-yellow-400"
            >
              <option value="">-- Select a League --</option>
              {initialData.leagues.map(league => (
                <option key={league.id} value={league.id}>{league.name}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block mb-2 font-semibold text-yellow-300">Select Team:</label>
            <select
              value={selectedTeamId}
              onChange={e => setSelectedTeamId(e.target.value)}
              disabled={!selectedLeagueId}
              className={`w-full bg-gray-800 text-white p-3 rounded-lg shadow-inner focus:outline-none focus:ring-2 focus:ring-yellow-400
                ${!selectedLeagueId ? 'opacity-50 cursor-not-allowed' : ''}
              `}
            >
              <option value="">-- Select a Team --</option>
              {availableTeams.map(team => (
                <option key={team.id} value={team.id}>{team.name}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Pitch and Bench + Player List */}
        {selectedTeamId && (
          <div className="w-full max-w-5xl flex flex-col md:flex-row gap-6">
            {/* Pitch + Bench */}
            <div className="flex flex-col md:flex-row gap-6 flex-grow">
              <Pitch pitchPlayers={pitchPlayers} onDropPlayer={handleDropPlayer} />
              <Bench benchPlayers={benchPlayers} onDropPlayer={handleDropPlayer} />
            </div>

            {/* All Team Players List */}
            <div className="bg-gray-800 rounded-lg p-4 max-w-sm w-full h-[350px] overflow-y-auto">
              <h2 className="text-yellow-400 font-bold mb-3 text-center">All Players</h2>
              {teamPlayers.length === 0 && (
                <p className="text-gray-400 italic text-center">No players found for this team.</p>
              )}
              <ul className="space-y-2">
                {teamPlayers.map(player => (
                  <li
                    key={player.id}
                    className={`p-2 rounded border ${
                      pitchPlayers.some(p => p.id === player.id)
                        ? 'border-green-400 bg-green-900'
                        : benchPlayers.some(p => p.id === player.id)
                        ? 'border-yellow-400 bg-yellow-900'
                        : 'border-gray-600'
                    }`}
                  >
                    <p className="font-semibold">{player.name}</p>
                    <p className="text-sm text-gray-300">{player.position}</p>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        )}
      </div>
    </DndProvider>
  );
}

export default App;
