import React, { useState, useEffect } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const ItemTypes = {
  PLAYER: 'player',
};

const dummyLeagues = [
  { id: 'ucl', name: 'UEFA Champions League' },
  { id: 'uel', name: 'UEFA Europa League' },
];

const dummyTeams = [
  { id: 'fenerbahce', name: 'Fenerbahçe', leagueId: 'ucl' },
  { id: 'galatasaray', name: 'Galatasaray', leagueId: 'ucl' },
  { id: 'besiktas', name: 'Beşiktaş', leagueId: 'uel' },
  { id: 'manu', name: 'Manchester United', leagueId: 'ucl' },
  { id: 'real', name: 'Real Madrid', leagueId: 'ucl' },
];

const dummyPlayers = [
  { id: '1', name: 'Altay Bayındır', teamId: 'fenerbahce', isHomegrown: true },
  { id: '2', name: 'Ferdi Kadıoğlu', teamId: 'fenerbahce', isHomegrown: true },
  { id: '3', name: 'Mert Hakan Yandaş', teamId: 'fenerbahce', isHomegrown: false },
  { id: '4', name: 'Arda Güler', teamId: 'real', isHomegrown: true },
  { id: '5', name: 'Cengiz Ünder', teamId: 'fenerbahce', isHomegrown: false },
  { id: '6', name: 'Fred', teamId: 'fenerbahce', isHomegrown: false },
  { id: '7', name: 'Edin Dzeko', teamId: 'fenerbahce', isHomegrown: false },
  { id: '8', name: 'Victor Nelsson', teamId: 'galatasaray', isHomegrown: false },
  { id: '9', name: 'Mauro Icardi', teamId: 'galatasaray', isHomegrown: false },
  { id: '10', name: 'Player 10', teamId: 'manu', isHomegrown: false },
  { id: '11', name: 'Player 11', teamId: 'manu', isHomegrown: true },
  { id: '12', name: 'Player 12', teamId: 'real', isHomegrown: false },
];

function Player({ player }) {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PLAYER,
    item: { id: player.id },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  // Hide original player while dragging so no ghost remains
  if (isDragging) {
    return <div ref={drag} style={{ display: 'none' }} />;
  }

  return (
    <div
      ref={drag}
      style={{
        cursor: 'move',
        padding: '8px 12px',
        margin: '4px',
        backgroundColor: player.isHomegrown ? '#4ade80' : '#60a5fa',
        color: 'white',
        borderRadius: '6px',
        fontWeight: 'bold',
        userSelect: 'none',
        textAlign: 'center',
        minWidth: 120,
      }}
      title={player.isHomegrown ? 'Homegrown Player' : 'Non-homegrown Player'}
    >
      {player.name}
    </div>
  );
}

function DropArea({ children, onDropPlayer, position, label, currentPlayers }) {
  const [{ isOver, canDrop }, drop] = useDrop({
    accept: ItemTypes.PLAYER,
    drop: (item) => {
      onDropPlayer(item.id, position);
    },
    canDrop: (item) => {
      // Allow all drops on bench, but only unique players on pitch
      if (position === 'bench') return true;
      return !currentPlayers.some((p) => p.id === item.id);
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  });

  const backgroundColor = isOver
    ? canDrop
      ? '#34d399'
      : '#f87171'
    : 'transparent';

  return (
    <div
      ref={drop}
      style={{
        minHeight: position === 'bench' ? '200px' : '500px',
        border: '2px dashed #999',
        borderRadius: '8px',
        padding: '10px',
        backgroundColor,
        flexGrow: position === 'bench' ? 1 : 2,
        marginRight: position === 'bench' ? 0 : '20px',
        display: 'flex',
        flexDirection: 'column',
        overflowY: 'auto',
      }}
    >
      <h3 style={{ marginBottom: '10px', color: '#fbbf24', fontWeight: 'bold' }}>{label}</h3>
      {children}
    </div>
  );
}

function Pitch({ pitchPlayers, onDropPlayer }) {
  const maxPitchPlayers = 11;

  return (
    <DropArea
      onDropPlayer={onDropPlayer}
      position="pitch"
      label="Football Pitch"
      currentPlayers={pitchPlayers}
    >
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(4, 1fr)',
          gridGap: '10px',
          flexGrow: 1,
          justifyContent: 'center',
        }}
      >
        {Array.from({ length: maxPitchPlayers }).map((_, i) => {
          const player = pitchPlayers[i];
          return player ? (
            <Player key={player.id} player={player} />
          ) : (
            <div key={i} style={{ height: '40px' }} />
          );
        })}
      </div>
      <small style={{ marginTop: 'auto', color: '#999', fontSize: '12px' }}>
        Drag players here (max 11)
      </small>
    </DropArea>
  );
}

function Bench({ benchPlayers, onDropPlayer }) {
  return (
    <DropArea
      onDropPlayer={onDropPlayer}
      position="bench"
      label="Bench"
      currentPlayers={benchPlayers}
    >
      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          gap: '8px',
          overflowY: 'auto',
          maxHeight: '480px',
        }}
      >
        {benchPlayers.length > 0 ? (
          benchPlayers.map((p) => <Player key={p.id} player={p} />)
        ) : (
          <div style={{ color: '#bbb' }}>No players on bench</div>
        )}
      </div>
      <small style={{ marginTop: 'auto', color: '#999', fontSize: '12px' }}>
        Drag players here
      </small>
    </DropArea>
  );
}

export default function App() {
  const [selectedLeagueId, setSelectedLeagueId] = useState('');
  const [selectedTeamId, setSelectedTeamId] = useState('');
  const [playerPositions, setPlayerPositions] = useState({}); // playerId => 'pitch' | 'bench'

  const filteredTeams = selectedLeagueId
    ? dummyTeams.filter((team) => team.leagueId === selectedLeagueId)
    : [];

  const teamPlayers = selectedTeamId
    ? dummyPlayers.filter((player) => player.teamId === selectedTeamId)
    : [];

  // Reset playerPositions on team change
  useEffect(() => {
    if (!selectedTeamId) {
      setPlayerPositions({});
      return;
    }
    const initPositions = {};
    teamPlayers.forEach((p) => (initPositions[p.id] = 'bench'));
    setPlayerPositions(initPositions);
  }, [selectedTeamId]);

  // Make sure to reflect playerPositions on filtered players:
  // Only players in the current team will be displayed
  // Players are assigned either to pitch or bench (never both)

  const pitchPlayers = teamPlayers.filter(
    (p) => playerPositions[p.id] === 'pitch'
  );
  const benchPlayers = teamPlayers.filter(
    (p) => playerPositions[p.id] === 'bench' || !playerPositions[p.id]
  );

  // Simplified UEFA rules validation
  const squadSize = teamPlayers.length;
  const homegrownCount = teamPlayers.filter((p) => p.isHomegrown).length;
  const nonHomegrownCount = squadSize - homegrownCount;

  const isSquadValid =
    squadSize <= 25 &&
    homegrownCount >= 8 &&
    nonHomegrownCount <= 17 &&
    pitchPlayers.length <= 11;

  const onDropPlayer = (playerId, newPosition) => {
    // Only allow max 11 players on pitch
    if (
      newPosition === 'pitch' &&
      pitchPlayers.length >= 11 &&
      playerPositions[playerId] !== 'pitch'
    ) {
      alert('Maximum 11 players allowed on the pitch.');
      return;
    }
    setPlayerPositions((prev) => ({ ...prev, [playerId]: newPosition }));
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div
        style={{
          padding: 20,
          fontFamily: "'Inter', sans-serif",
          color: '#f9fafb',
          backgroundColor: '#0f172a',
          minHeight: '100vh',
        }}
      >
        <h1
          style={{
            textAlign: 'center',
            marginBottom: 24,
            fontSize: '2rem',
            fontWeight: 'bold',
            color: '#60a5fa',
          }}
        >
          UEFA Squad Builder
        </h1>

        <div
          style={{
            display: 'flex',
            gap: '16px',
            justifyContent: 'center',
            marginBottom: 24,
            flexWrap: 'wrap',
          }}
        >
          <select
            value={selectedLeagueId}
            onChange={(e) => {
              setSelectedLeagueId(e.target.value);
              setSelectedTeamId('');
            }}
            style={{
              padding: '8px 12px',
              borderRadius: 6,
              fontSize: 16,
              border: '1px solid #334155',
            }}
          >
            <option value="">-- Select League --</option>
            {dummyLeagues.map((league) => (
              <option key={league.id} value={league.id}>
                {league.name}
              </option>
            ))}
          </select>

          <select
            value={selectedTeamId}
            onChange={(e) => setSelectedTeamId(e.target.value)}
            disabled={!selectedLeagueId}
            style={{
              padding: '8px 12px',
              borderRadius: 6,
              fontSize: 16,
              border: '1px solid #334155',
              opacity: !selectedLeagueId ? 0.5 : 1,
              cursor: !selectedLeagueId ? 'not-allowed' : 'auto',
            }}
          >
            <option value="">-- Select Team --</option>
            {filteredTeams.map((team) => (
              <option key={team.id} value={team.id}>
                {team.name}
              </option>
            ))}
          </select>
        </div>

        {!selectedTeamId && (
          <p style={{ textAlign: 'center', color: '#94a3b8' }}>
            Please select a league and a team to build your squad.
          </p>
        )}

        {selectedTeamId && (
          <>
            <div
              style={{
                display: 'flex',
                gap: 20,
                justifyContent: 'center',
                flexWrap: 'wrap',
              }}
            >
              <Pitch pitchPlayers={pitchPlayers} onDropPlayer={onDropPlayer} />
              <Bench benchPlayers={benchPlayers} onDropPlayer={onDropPlayer} />
            </div>

            <div
              style={{
                maxWidth: 600,
                margin: '24px auto',
                color: '#cbd5e1',
                fontSize: 16,
              }}
            >
              <p>
                Squad size: <b>{squadSize}</b> players (Max 25)
              </p>
              <p>
                Homegrown players: <b>{homegrownCount}</b> (Min 8)
              </p>
              <p>
                Non-homegrown players: <b>{nonHomegrownCount}</b> (Max 17)
              </p>
              <p>
                Players on pitch: <b>{pitchPlayers.length}</b> (Max 11)
              </p>

              {!isSquadValid && (
                <p style={{ color: '#f87171', fontWeight: 'bold' }}>
                  Squad does not meet UEFA requirements! Please adjust your
                  players.
                </p>
              )}
              {isSquadValid && (
                <p style={{ color: '#4ade80', fontWeight: 'bold' }}>
                  Squad meets UEFA requirements! Well done.
                </p>
              )}
            </div>
          </>
        )}
      </div>
    </DndProvider>
  );
}
