import React, { useState, useEffect } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

const ItemTypes = {
  PLAYER: 'player',
};

const dummyLeagues = [
  { id: 'ucl', name: 'UEFA Champions League' },
  { id: 'uel', name: 'UEFA Europa League' },
  { id: 'ucl_conf', name: 'UEFA Conference League' },
];

const dummyTeams = [
  { id: 'fenerbahce', name: 'Fenerbahçe', leagueId: 'ucl' },
  { id: 'galatasaray', name: 'Galatasaray', leagueId: 'ucl' },
  { id: 'besiktas', name: 'Beşiktaş', leagueId: 'uel' },
  { id: 'samsunspor', name: 'Samsunspor', leagueId: 'uel' },
  { id: 'basaksehir', name: 'Başakşehir', leagueId: 'ucl_conf' },
];

const dummyPlayers = [
  // Fenerbahçe (UCL)
  { id: 'fb-livakovic', name: 'Dominik Livaković', teamId: 'fenerbahce', isHomegrown: false },
  { id: 'fb-irfan-can-e', name: 'İrfan Can Eğribayat', teamId: 'fenerbahce', isHomegrown: true },
