  { id: 'fb-carlos', name: 'Diego Carlos', teamId: 'fenerbahce', isHomegrown: false },
  { id: 'fb-yusuf-akcicek', name: 'Yusuf Akcicek', teamId: 'fenerbahce', isHomegrown: true },
  { id: 'fb-milan', name: 'Milan Skriniar', teamId: 'fenerbahce', isHomegrown: false },
