  { id: 'fb-djiku', name: 'Alexander Djiku', teamId: 'fenerbahce', isHomegrown: false },
  { id: 'fb-becao', name: 'Rodrigo Becão', teamId: 'fenerbahce', isHomegrown: false },
  { id: 'fb-oosterwolde', name: 'Jayden Oosterwolde', teamId: 'fenerbahce', isHomegrown: false },
  { id: 'fb-serdar-aziz', name: 'Serdar Aziz', teamId: 'fenerbahce', isHomegrown: true },
  { id: 'fb-mert-muldur', name: 'Mert Müldür', teamId: 'fenerbahce', isHomegrown: true },
  { id: 'fb-osayi-samuel', name: 'Bright Osayi-Samuel', teamId: 'fenerbahce', isHomegrown: false },
  { id: 'fb-oguz-aydin', name: 'Oguz Aydin', teamId: 'fenerbahce', isHomegrown: false },
  { id: 'fb-ismail-yuksek', name: 'İsmail Yüksek', teamId: 'fenerbahce', isHomegrown: true },
  { id: 'fb-fred', name: 'Fred', teamId: 'fenerbahce', isHomegrown: false },
  { id: 'fb-szymanski', name: 'Sebastian Szymański', teamId: 'fenerbahce', isHomegrown: false },
  { id: 'fb-mert-hakan', name: 'Mert Hakan Yandaş', teamId: 'fenerbahce', isHomegrown: true },
  { id: 'fb-cengiz', name: 'Cengiz Ünder', teamId: 'fenerbahce', isHomegrown: true },
