function Player({ player }) {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PLAYER,
    item: { id: player.id },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  return (
    <div
      ref={drag}
      style={{
        opacity: isDragging ? 0.4 : 1,
        cursor: 'move',
        backgroundColor: player.isHomegrown ? '#4ade80' : '#60a5fa',
        color: 'white',
        padding: '8px 12px',
        margin: '4px',
        borderRadius: 6,
        fontWeight: 'bold',
        textAlign: 'center',
        minWidth: 120,
        userSelect: 'none',
      }}
      title={player.isHomegrown ? 'Homegrown Player' : 'Non-homegrown Player'}
    >
      {player.name}
    </div>
  );
}

function DropArea({ children, onDropPlayer, position }) {
  const [{ isOver }, drop] = useDrop({
    accept: ItemTypes.PLAYER,
    drop: (item) => {
      onDropPlayer(item.id, position);
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  });

  return (
    <div
      ref={drop}
      style={{
        backgroundColor: isOver ? '#1e293b' : '#0f172a',
        border: '2px dashed #334155',
        borderRadius: 10,
        padding: 16,
        minHeight: 200,
        flex: 1,
        overflowY: 'auto',
      }}
    >
      {children}
    </div>
  );
}

function Pitch({ players, onDropPlayer }) {
  return (
    <DropArea onDropPlayer={onDropPlayer} position="pitch">
      <h2 style={{ color: '#fbbf24' }}>Eligible Squad (Pitch)</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
        {players.map((p) => (
          <Player key={p.id} player={p} />
        ))}
      </div>
    </DropArea>
  );
}

function Bench({ players, onDropPlayer }) {
  return (
    <DropArea onDropPlayer={onDropPlayer} position="bench">
      <h2 style={{ color: '#94a3b8' }}>Full Squad (Bench)</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
        {players.map((p) => (
          <Player key={p.id} player={p} />
        ))}
      </div>
    </DropArea>
  );
}

export default function App() {
  const [selectedLeague, setSelectedLeague] = useState('');
  const [selectedTeam, setSelectedTeam] = useState('');
  const [playerPositions, setPlayerPositions] = useState({}); // playerId -> 'pitch' or 'bench'

  const teams = dummyTeams.filter((t) => t.leagueId === selectedLeague);
  const players = dummyPlayers.filter((p) => p.teamId === selectedTeam);

  // Initialize all players to bench on team change
  useEffect(() => {
    if (selectedTeam) {
      const newPositions = {};
      players.forEach((p) => {
        newPositions[p.id] = 'bench'; // everyone on bench initially
      });
      setPlayerPositions(newPositions);
    }
  }, [selectedTeam]);

  const pitchPlayers = players.filter((p) => playerPositions[p.id] === 'pitch');
  const benchPlayers = players.filter((p) => playerPositions[p.id] === 'bench');

  // UEFA validation on pitch only
  const pitchSquadSize = pitchPlayers.length;
  const homegrownCount = pitchPlayers.filter((p) => p.isHomegrown).length;
  const nonHomegrownCount = pitchSquadSize - homegrownCount;

  const isSquadValid =
    pitchSquadSize <= 25 &&
    homegrownCount >= 8 &&
    nonHomegrownCount <= 17;

  const onDropPlayer = (id, toPosition) => {
    if (toPosition === 'pitch' && pitchPlayers.length >= 25 && playerPositions[id] !== 'pitch') {
      alert('Maximum 25 players allowed in the eligible squad (pitch).');
      return;
    }
    setPlayerPositions((prev) => ({
      ...prev,
      [id]: toPosition,
    }));
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div style={{ backgroundColor: '#0f172a', color: '#fff', minHeight: '100vh', padding: 20, fontFamily: 'sans-serif' }}>
        <h1 style={{ color: '#60a5fa' }}>UEFA Squad Builder 2025/26</h1>

        <div style={{ display: 'flex', gap: 12, marginBottom: 20 }}>
          <select
            value={selectedLeague}
            onChange={(e) => {
              setSelectedLeague(e.target.value);
              setSelectedTeam('');
              setPlayerPositions({});
            }}
          >
            <option value="">-- Select League --</option>
            {dummyLeagues.map((l) => (
              <option key={l.id} value={l.id}>{l.name}</option>
            ))}
          </select>

          <select
            value={selectedTeam}
            onChange={(e) => setSelectedTeam(e.target.value)}
            disabled={!selectedLeague}
          >
            <option value="">-- Select Team --</option>
            {teams.map((t) => (
              <option key={t.id} value={t.id}>{t.name}</option>
            ))}
          </select>
        </div>

        {!selectedTeam && <p style={{ color: '#cbd5e1' }}>Please select a league and a team.</p>}

        {selectedTeam && (
          <>
            <div style={{ display: 'flex', gap: 20 }}>
              <Pitch players={pitchPlayers} onDropPlayer={onDropPlayer} />
              <Bench players={benchPlayers} onDropPlayer={onDropPlayer} />
            </div>

            <div style={{ maxWidth: 600, margin: '20px auto', color: '#cbd5e1' }}>
              <p>Full squad size: <b>{players.length}</b></p>
              <p>Eligible squad (pitch) size: <b>{pitchSquadSize}</b> (Max 25)</p>
              <p>Homegrown players on pitch: <b>{homegrownCount}</b> (Min 8)</p>
              <p>Non-homegrown players on pitch: <b>{nonHomegrownCount}</b> (Max 17)</p>

              {!isSquadValid ? (
                <p style={{ color: '#f87171', fontWeight: 'bold' }}>Eligible squad does not meet UEFA requirements!</p>
              ) : (
                <p style={{ color: '#4ade80', fontWeight: 'bold' }}>Eligible squad meets UEFA requirements!</p>
              )}
            </div>
          </>
        )}
      </div>
    </DndProvider>
  );
}
