import React, { useState, useEffect, createContext, useContext, useCallback } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, signInWithCustomToken, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, getDocs, doc, setDoc, query, where, onSnapshot } from 'firebase/firestore';

// Create a context for Firebase services
const FirebaseContext = createContext(null);

// Firebase Provider component
const FirebaseProvider = ({ children }) => {
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [userId, setUserId] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);

  useEffect(() => {
    // These global variables are provided by the Canvas environment.
    const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
    const firebaseConfigString = typeof __firebase_config !== 'undefined' ? __firebase_config : '{}';
    const firebaseConfig = JSON.parse(firebaseConfigString);

    // Set Firestore log level to debug for more detailed output
    // setLogLevel('debug');

    if (Object.keys(firebaseConfig).length === 0) {
      console.error("Firebase config is not provided. Please ensure __firebase_config is set, or provide it directly for local development.");
      return;
    }

    try {
      const app = initializeApp(firebaseConfig);
      const firestoreDb = getFirestore(app);
      const firebaseAuth = getAuth(app);

      setDb(firestoreDb);
      setAuth(firebaseAuth);

      // onAuthStateChanged is the observer that listens for auth state changes.
      // It's the most reliable way to get the current user.
      const unsubscribe = onAuthStateChanged(firebaseAuth, async (user) => {
        if (user) {
          // If the user object exists, it means they are authenticated.
          console.log("User is authenticated with UID:", user.uid);
          setUserId(user.uid);
          // Now that we have a confirmed user, we can mark auth as ready.
          setIsAuthReady(true);
        } else {
          // If the user is null, it means they are not signed in.
          // We will attempt to sign them in.
          console.log("User is not authenticated. Attempting sign-in...");
          try {
            // The sign-in attempt itself will trigger the onAuthStateChanged observer again.
            // On the next trigger, the 'user' object should exist.
            if (typeof __initial_auth_token !== 'undefined' && __initial_auth_token) {
              await signInWithCustomToken(firebaseAuth, __initial_auth_token);
            } else {
              await signInAnonymously(firebaseAuth);
            }
          } catch (error) {
            console.error("Failed to sign in:", error);
            // If sign-in fails, the app will remain in the loading state.
            // You could set an error state here to show a message to the user.
          }
        }
      });

      return () => unsubscribe();
    } catch (error) {
      console.error("Error initializing Firebase:", error);
    }
  }, []);

  return (
    <FirebaseContext.Provider value={{ db, auth, userId, isAuthReady }}>
      {children}
    </FirebaseContext.Provider>
  );
};

// Custom hook to use Firebase services
const useFirebase = () => useContext(FirebaseContext);

// Main App component
const App = () => {
  const { db, userId, isAuthReady } = useFirebase();
  const [countries, setCountries] = useState([]);
  const [teams, setTeams] = useState([]);
  const [players, setPlayers] = useState([]);
  const [selectedCountryId, setSelectedCountryId] = useState('');
  const [selectedTeamId, setSelectedTeamId] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Dummy data to populate Firestore if empty
  const initialData = {
    countries: [
      { id: 'turkey', name: 'Turkey' },
      { id: 'england', name: 'England' },
      { id: 'spain', name: 'Spain' },
    ],
    teams: [
      { id: 'fenerbahce', name: 'Fenerbahçe', countryId: 'turkey' },
      { id: 'galatasaray', name: 'Galatasaray', countryId: 'turkey' },
      { id: 'besiktas', name: 'Beşiktaş', countryId: 'turkey' },
      { id: 'manchester_united', name: 'Manchester United', countryId: 'england' },
      { id: 'real_madrid', name: 'Real Madrid', countryId: 'spain' },
    ],
    players: [
      { id: 'player1', name: 'Altay Bayındır', teamId: 'fenerbahce', age: 26, nationality: 'Turkish', clubTrained: true, associationTrained: true, position: 'Goalkeeper', isUnder21: false },
      { id: 'player2', name: 'Ferdi Kadıoğlu', teamId: 'fenerbahce', age: 24, nationality: 'Turkish', clubTrained: true, associationTrained: true, position: 'Defender', isUnder21: false },
      { id: 'player3', name: 'Mert Hakan Yandaş', teamId: 'fenerbahce', age: 29, nationality: 'Turkish', clubTrained: false, associationTrained: true, position: 'Midfielder', isUnder21: false },
      { id: 'player4', name: 'Arda Güler', teamId: 'real_madrid', age: 19, nationality: 'Turkish', clubTrained: true, associationTrained: true, position: 'Midfielder', isUnder21: true },
      { id: 'player5', name: 'Cengiz Ünder', teamId: 'fenerbahce', age: 26, nationality: 'Turkish', clubTrained: false, associationTrained: false, position: 'Forward', isUnder21: false },
      { id: 'player6', name: 'Fred', teamId: 'fenerbahce', age: 31, nationality: 'Brazilian', clubTrained: false, associationTrained: false, position: 'Midfielder', isUnder21: false },
      { id: 'player7', name: 'Edin Dzeko', teamId: 'fenerbahce', age: 38, nationality: 'Bosnian', clubTrained: false, associationTrained: false, position: 'Forward', isUnder21: false },
      { id: 'player8', name: 'Victor Nelsson', teamId: 'galatasaray', age: 25, nationality: 'Danish', clubTrained: false, associationTrained: false, position: 'Defender', isUnder21: false },
      { id: 'player9', name: 'Mauro Icardi', teamId: 'galatasaray', age: 31, nationality: 'Argentine', clubTrained: false, associationTrained: false, position: 'Forward', isUnder21: false },
    ],
  };

  // Function to populate Firestore with dummy data if collections are empty
  // Wrapped in useCallback to prevent re-creation on every render, fixing potential infinite loops.
  const populateFirestoreWithDummyData = useCallback(async () => {
    if (!db || !userId) return;

    const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
    const privateUserPath = `artifacts/${appId}/users/${userId}`;

    try {
      // Check and add countries
      const countriesRef = collection(db, `${privateUserPath}/countries`);
      const countryDocs = await getDocs(countriesRef);
      if (countryDocs.empty) {
        console.log("Populating countries...");
        for (const country of initialData.countries) {
          await setDoc(doc(countriesRef, country.id), country);
        }
      }

      // Check and add teams
      const teamsRef = collection(db, `${privateUserPath}/teams`);
      const teamDocs = await getDocs(teamsRef);
      if (teamDocs.empty) {
        console.log("Populating teams...");
        for (const team of initialData.teams) {
          await setDoc(doc(teamsRef, team.id), team);
        }
      }

      // Check and add players
      const playersRef = collection(db, `${privateUserPath}/players`);
      const playerDocs = await getDocs(playersRef);
      if (playerDocs.empty) {
        console.log("Populating players...");
        for (const player of initialData.players) {
          await setDoc(doc(playersRef, player.id), player);
        }
      }
      console.log("Dummy data population check complete.");
    } catch (err) {
      console.error("Error populating dummy data:", err);
      setError("Failed to populate initial data.");
    }
  }, [db, userId]); // Dependencies for useCallback

  // Effect to fetch countries when Firebase is ready
  useEffect(() => {
    if (!db || !isAuthReady || !userId) return;

    // This function will now run once after auth is ready
    populateFirestoreWithDummyData();

    const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
    const privateUserPath = `artifacts/${appId}/users/${userId}`;
    const countriesColRef = collection(db, `${privateUserPath}/countries`);

    // Listen for real-time updates on countries
    const unsubscribeCountries = onSnapshot(countriesColRef, (snapshot) => {
      const countriesData = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setCountries(countriesData);
      setLoading(false);
    }, (err) => {
      console.error("Error fetching countries:", err);
      setError("Failed to load countries.");
      setLoading(false);
    });

    return () => unsubscribeCountries();
  }, [db, isAuthReady, userId, populateFirestoreWithDummyData]);

  // Effect to fetch teams when a country is selected
  useEffect(() => {
    if (!db || !selectedCountryId || !isAuthReady || !userId) {
      setTeams([]); // Clear teams if no country selected or not ready
      setPlayers([]); // Also clear players
      setSelectedTeamId('');
      return;
    }

    const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
    const privateUserPath = `artifacts/${appId}/users/${userId}`;
    const teamsColRef = collection(db, `${privateUserPath}/teams`);
    const q = query(teamsColRef, where('countryId', '==', selectedCountryId));

    const unsubscribeTeams = onSnapshot(q, (snapshot) => {
      const teamsData = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setTeams(teamsData);
      setSelectedTeamId(''); // Reset selected team when country changes
    }, (err) => {
      console.error("Error fetching teams:", err);
      setError("Failed to load teams.");
    });

    return () => unsubscribeTeams();
  }, [db, selectedCountryId, isAuthReady, userId]);

  // Effect to fetch players when a team is selected
  useEffect(() => {
    if (!db || !selectedTeamId || !isAuthReady || !userId) {
      setPlayers([]); // Clear players if no team selected or not ready
      return;
    }

    const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
    const privateUserPath = `artifacts/${appId}/users/${userId}`;
    const playersColRef = collection(db, `${privateUserPath}/players`);
    const q = query(playersColRef, where('teamId', '==', selectedTeamId));

    const unsubscribePlayers = onSnapshot(q, (snapshot) => {
      const playersData = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setPlayers(playersData);
    }, (err) => {
      console.error("Error fetching players:", err);
      setError("Failed to load players.");
    });

    return () => unsubscribePlayers();
  }, [db, selectedTeamId, isAuthReady, userId]);

  if (!isAuthReady || loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-900 text-white">
        <div className="text-xl">Loading application and user data...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-red-800 text-white p-4">
        <div className="text-xl">Error: {error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-gray-100 font-sans p-4 sm:p-8 flex flex-col items-center">
      {/* User ID Display - MANDATORY for multi-user apps */}
      {userId && (
        <div className="w-full max-w-4xl text-xs text-gray-400 mb-4 rounded-md p-2 bg-gray-800 shadow-md">
          User ID: <span className="font-mono text-blue-300 break-all">{userId}</span>
        </div>
      )}

      <h1 className="text-4xl font-extrabold mb-8 text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-600 rounded-lg p-2">
        CL Squad Builder
      </h1>

      <div className="w-full max-w-4xl bg-gray-800 rounded-xl shadow-2xl p-6 sm:p-8 mb-8">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Country Selection */}
          <div>
            <label htmlFor="country-select" className="block text-lg font-semibold text-gray-200 mb-2">
              Select Country:
            </label>
            <div className="relative">
              <select
                id="country-select"
                className="block w-full bg-gray-700 border border-gray-600 text-white py-2 px-4 pr-8 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 appearance-none transition-all duration-300 ease-in-out"
                value={selectedCountryId}
                onChange={(e) => setSelectedCountryId(e.target.value)}
              >
                <option value="">-- Select a Country --</option>
                {countries.map((country) => (
                  <option key={country.id} value={country.id}>
                    {country.name}
                  </option>
                ))}
              </select>
              <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-400">
                <svg className="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
                  <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z" />
                </svg>
              </div>
            </div>
          </div>

          {/* Team Selection */}
          <div>
            <label htmlFor="team-select" className="block text-lg font-semibold text-gray-200 mb-2">
              Select Team:
            </label>
            <div className="relative">
              <select
                id="team-select"
                className="block w-full bg-gray-700 border border-gray-600 text-white py-2 px-4 pr-8 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-purple-500 appearance-none transition-all duration-300 ease-in-out"
                value={selectedTeamId}
                onChange={(e) => setSelectedTeamId(e.target.value)}
                disabled={!selectedCountryId || teams.length === 0}
              >
                <option value="">-- Select a Team --</option>
                {teams.map((team) => (
                  <option key={team.id} value={team.id}>
                    {team.name}
                  </option>
                ))}
              </select>
              <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-400">
                <svg className="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
                  <path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z" />
                </svg>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Players List */}
      {selectedTeamId && (
         <div className="w-full max-w-4xl bg-gray-800 rounded-xl shadow-2xl p-6 sm:p-8">
            <h2 className="text-3xl font-bold mb-6 text-blue-400">
                Players for {teams.find(t => t.id === selectedTeamId)?.name || '...'}
            </h2>
            {players.length > 0 ? (
                <ul className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                    {players.map((player) => (
                    <li key={player.id} className="bg-gray-700 p-4 rounded-lg shadow-lg hover:shadow-xl hover:-translate-y-1 transition-all duration-300 ease-in-out border border-gray-600">
                        <p className="text-xl font-semibold text-white">{player.name}</p>
                        <p className="text-gray-300 text-sm">Age: {player.age}</p>
                        <p className="text-gray-300 text-sm">Nationality: {player.nationality}</p>
                        <p className="text-gray-300 text-sm">Position: {player.position}</p>
                        <p className="text-gray-300 text-sm">
                        Club-trained: <span className={player.clubTrained ? 'text-green-400 font-semibold' : 'text-red-400'}>{player.clubTrained ? 'Yes' : 'No'}</span>
                        </p>
                        <p className="text-gray-300 text-sm">
                        Assoc-trained: <span className={player.associationTrained ? 'text-green-400 font-semibold' : 'text-red-400'}>{player.associationTrained ? 'Yes' : 'No'}</span>
                        </p>
                        <p className="text-gray-300 text-sm">
                        U21 Player: <span className={player.isUnder21 ? 'text-yellow-400 font-semibold' : 'text-gray-400'}>{player.isUnder21 ? 'Yes' : 'No'}</span>
                        </p>
                    </li>
                    ))}
                </ul>
            ) : (
                <div className="text-center text-gray-400">
                    No players found for this team.
                </div>
            )}
        </div>
      )}
    </div>
  );
};

// Root component that provides Firebase context
const SquadBuilderApp = () => (
  <FirebaseProvider>
    <App />
  </FirebaseProvider>
);

export default SquadBuilderApp;
