import React, { useState, useEffect, useMemo } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { XCircle, CheckCircle, AlertTriangle, Shield, School, Users } from 'lucide-react';

// --- Constants and Data ---
const ItemTypes = {
  PLAYER: 'player',
};

const TRAINING_STATUS = {
  CLUB: 'club-trained',
  ASSOC: 'association-trained',
  NONE: 'non-homegrown',
};

const POSITIONS = {
  GK: 'Goalkeeper',
  OUTFIELD: 'Outfield',
};

// --- Data with Detailed Player Info ---
// NOTE: Training status is an estimation for demonstration purposes.
const dummyLeagues = [
  { id: 'ucl', name: 'UEFA Champions League' },
  { id: 'uel', name: 'UEFA Europa League' },
  { id: 'uecl', name: 'UEFA Europa Conference League' },
];

const dummyTeams = [
  { id: 'fenerbahce', name: 'Fenerbahçe', leagueId: 'ucl' },
  { id: 'galatasaray', name: 'Galatasaray', leagueId: 'ucl' },
  { id: 'besiktas', name: 'Beşiktaş', leagueId: 'uel' },
];

const dummyPlayers = [
  // Fenerbahçe (UCL)
  { id: 'fb-livakovic', name: 'Dominik Livaković', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.GK },
  { id: 'fb-irfan-can-e', name: 'İrfan Can Eğribayat', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.GK },
  { id: 'fb-ertugrul', name: 'Ertuğrul Çetin', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.GK },
  { id: 'fb-caglar', name: 'Çağlar Söyüncü', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
  { id: 'fb-djiku', name: 'Alexander Djiku', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'fb-becao', name: 'Rodrigo Becão', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'fb-oosterwolde', name: 'Jayden Oosterwolde', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'fb-mert-muldur', name: 'Mert Müldür', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
  { id: 'fb-ismail-yuksek', name: 'İsmail Yüksek', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
  { id: 'fb-fred', name: 'Fred', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'fb-szymanski', name: 'Sebastian Szymański', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'fb-mert-hakan', name: 'Mert Hakan Yandaş', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
  { id: 'fb-irfan-can-k', name: 'İrfan Can Kahveci', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
  { id: 'fb-tadic', name: 'Dušan Tadić', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'fb-dzeko', name: 'Edin Džeko', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'fb-batshuayi', name: 'Michy Batshuayi', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'fb-ferdi', name: 'Ferdi Kadıoğlu', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
  { id: 'fb-arda-guler', name: 'Arda Güler', teamId: 'fenerbahce', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD }, // Example of a young club-trained player

  // Galatasaray (UCL)
  { id: 'gs-muslera', name: 'Fernando Muslera', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.GK },
  { id: 'gs-gunay', name: 'Günay Güvenç', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.GK },
  { id: 'gs-sanchez', name: 'Davinson Sánchez', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'gs-abdulkerim', name: 'Abdülkerim Bardakcı', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
  { id: 'gs-kaan-ayhan', name: 'Kaan Ayhan', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
  { id: 'gs-torreira', name: 'Lucas Torreira', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'gs-kerem-a', name: 'Kerem Aktürkoğlu', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
  { id: 'gs-icardi', name: 'Mauro Icardi', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'gs-yunus', name: 'Yunus Akgün', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
  { id: 'gs-emin', name: 'Emin Bayram', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
  { id: 'gs-baris-alper', name: 'Barış Alper Yılmaz', teamId: 'galatasaray', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },

  // Beşiktaş (UEL)
  { id: 'bjk-mert-gunok', name: 'Mert Günok', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.GK },
  { id: 'bjk-ersin', name: 'Ersin Destanoğlu', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.GK },
  { id: 'bjk-colley', name: 'Omar Colley', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'bjk-gedson', name: 'Gedson Fernandes', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.NONE, position: POSITIONS.OUTFIELD },
  { id: 'bjk-salih-ucan', name: 'Salih Uçan', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
  { id: 'bjk-semih', name: 'Semih Kılıçsoy', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
  { id: 'bjk-cenk-tosun', name: 'Cenk Tosun', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.ASSOC, position: POSITIONS.OUTFIELD },
  { id: 'bjk-necip', name: 'Necip Uysal', teamId: 'besiktas', trainingStatus: TRAINING_STATUS.CLUB, position: POSITIONS.OUTFIELD },
];

const UEFA_RULES = {
  MAX_SQUAD_SIZE: 25,
  MIN_GOALKEEPERS: 2,
  MIN_TOTAL_HOMEGROWN: 8,
  MIN_CLUB_TRAINED: 4,
  MAX_ASSOC_TRAINED: 4,
};

// --- Helper Components ---

const PlayerIcon = ({ status }) => {
  const icons = {
    [TRAINING_STATUS.CLUB]: <School className="w-4 h-4 mr-2 text-amber-300" />,
    [TRAINING_STATUS.ASSOC]: <Users className="w-4 h-4 mr-2 text-sky-300" />,
    [TRAINING_STATUS.NONE]: <Shield className="w-4 h-4 mr-2 text-slate-400" />,
  };
  return icons[status] || null;
};

// --- Main Components ---

function Player({ player }) {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.PLAYER,
    item: { id: player.id },
    collect: (monitor) => ({ isDragging: !!monitor.isDragging() }),
  }));

  const playerStyles = {
    [TRAINING_STATUS.CLUB]: 'bg-amber-600 hover:bg-amber-500',
    [TRAINING_STATUS.ASSOC]: 'bg-sky-600 hover:bg-sky-500',
    [TRAINING_STATUS.NONE]: 'bg-slate-600 hover:bg-slate-500',
  };

  const tooltipText = {
    [TRAINING_STATUS.CLUB]: 'Club-Trained Player',
    [TRAINING_STATUS.ASSOC]: 'Association-Trained Player',
    [TRAINING_STATUS.NONE]: 'Non-Homegrown Player',
  };

  return (
    <div
      ref={drag}
      className={`p-2 px-3 rounded-md font-semibold text-white shadow-lg transition-all duration-200 cursor-move select-none flex items-center ${playerStyles[player.trainingStatus]} ${isDragging ? 'opacity-40 scale-105' : 'opacity-100'}`}
      title={tooltipText[player.trainingStatus]}
    >
      <PlayerIcon status={player.trainingStatus} />
      <span>{player.name}</span>
      {player.position === POSITIONS.GK && <span className="ml-2 text-xs opacity-80">(GK)</span>}
    </div>
  );
}

function DropZone({ children, onDropPlayer, position, title, titleColor, canDrop }) {
    const [{ isOver, isDropAllowed }, drop] = useDrop({
      accept: ItemTypes.PLAYER,
      drop: (item) => onDropPlayer(item.id, position),
      canDrop: (item, monitor) => canDrop(item, position),
      collect: (monitor) => ({
        isOver: monitor.isOver(),
        isDropAllowed: monitor.canDrop(),
      }),
    });

    const baseBorder = "border-slate-600";
    const hoverBorder = isDropAllowed ? "border-emerald-500" : "border-red-500";
    const borderColor = isOver ? hoverBorder : baseBorder;

    return (
      <div ref={drop} className={`bg-slate-800/50 border-2 border-dashed rounded-lg p-4 flex-1 transition-colors duration-300 ${borderColor}`}>
        <h2 className={`text-xl font-semibold mb-4 ${titleColor}`}>{title}</h2>
        <div className="flex flex-wrap gap-2 min-h-[200px] content-start">
          {children}
        </div>
      </div>
    );
  }

function SquadStats({ pitchPlayers }) {
  const clubTrainedCount = pitchPlayers.filter(p => p.trainingStatus === TRAINING_STATUS.CLUB).length;
  const assocTrainedCount = pitchPlayers.filter(p => p.trainingStatus === TRAINING_STATUS.ASSOC).length;
  const totalHomegrownCount = clubTrainedCount + assocTrainedCount;
  const gkCount = pitchPlayers.filter(p => p.position === POSITIONS.GK).length;

  // Determine effective squad size
  const homegrownDeficit = Math.max(0, UEFA_RULES.MIN_TOTAL_HOMEGROWN - totalHomegrownCount);
  const maxSquadSize = UEFA_RULES.MAX_SQUAD_SIZE - homegrownDeficit;

  const validationChecks = [
    { label: 'Goalkeepers on Pitch', value: `${gkCount}`, rule: `Min ${UEFA_RULES.MIN_GOALKEEPERS}`, isValid: gkCount >= UEFA_RULES.MIN_GOALKEEPERS },
    { label: 'Club-Trained Players', value: `${clubTrainedCount}`, rule: `Min ${UEFA_RULES.MIN_CLUB_TRAINED}`, isValid: clubTrainedCount >= UEFA_RULES.MIN_CLUB_TRAINED },
    { label: 'Association-Trained', value: `${assocTrainedCount}`, rule: `Max ${UEFA_RULES.MAX_ASSOC_TRAINED}`, isValid: assocTrainedCount <= UEFA_RULES.MAX_ASSOC_TRAINED },
    { label: 'Total Homegrown', value: `${totalHomegrownCount}`, rule: `Min ${UEFA_RULES.MIN_TOTAL_HOMEGROWN}`, isValid: totalHomegrownCount >= UEFA_RULES.MIN_TOTAL_HOMEGROWN },
    { label: 'Eligible Squad Size', value: `${pitchPlayers.length}`, rule: `Max ${maxSquadSize}`, isValid: pitchPlayers.length <= maxSquadSize },
  ];

  const isSquadFullyValid = validationChecks.every(check => check.isValid);

  return (
    <div className="mt-6 p-4 bg-slate-800 rounded-lg max-w-4xl mx-auto text-slate-300 shadow-xl">
      <h3 className="text-lg font-bold text-white mb-4">UEFA Squad Rules Validation (List A)</h3>
       <p className="text-sm text-slate-400 mb-4">
        Your maximum squad size is <b className="text-white">{maxSquadSize}</b> because you have a homegrown deficit of <b className="text-white">{homegrownDeficit}</b> player(s).
      </p>
      <div className="space-y-2">
        {validationChecks.map(({ label, value, rule, isValid }) => (
          <div key={label} className="flex justify-between items-center p-2 bg-slate-700/50 rounded-md">
            <div className="flex items-center">
              {isValid ? <CheckCircle className="text-green-500 mr-3" size={20} /> : <XCircle className="text-red-500 mr-3" size={20} />}
              <span className="font-semibold text-white">{label}</span>
              <span className="text-sm text-slate-400 ml-2">({rule})</span>
            </div>
            <span className={`font-bold text-lg ${isValid ? 'text-green-400' : 'text-red-400'}`}>{value}</span>
          </div>
        ))}
      </div>
      <div className={`mt-4 p-3 rounded-lg font-bold text-center text-lg transition-colors ${isSquadFullyValid ? 'bg-green-500/20 text-green-400' : 'bg-red-500/20 text-red-400'}`}>
        {isSquadFullyValid ? 'This squad meets all UEFA requirements!' : 'This squad does not meet UEFA requirements!'}
      </div>
    </div>
  );
}

function Notification({ message, onDismiss }) {
    if (!message) return null;
    useEffect(() => {
        const timer = setTimeout(onDismiss, 3000);
        return () => clearTimeout(timer);
    }, [message, onDismiss]);
    return (
        <div className="fixed top-5 right-5 bg-red-600 text-white py-2 px-4 rounded-lg shadow-lg flex items-center animate-pulse">
            <AlertTriangle className="mr-2" />
            {message}
        </div>
    );
}


// --- Main App Component ---

export default function App() {
  const [selectedLeague, setSelectedLeague] = useState('');
  const [selectedTeam, setSelectedTeam] = useState('');
  const [playerPositions, setPlayerPositions] = useState({});
  const [notification, setNotification] = useState(null);

  const availableTeams = useMemo(() => dummyTeams.filter(t => t.leagueId === selectedLeague), [selectedLeague]);
  const allPlayersForTeam = useMemo(() => dummyPlayers.filter(p => p.teamId === selectedTeam), [selectedTeam]);

  useEffect(() => {
    if (selectedTeam) {
      const initialPositions = allPlayersForTeam.reduce((acc, player) => {
        acc[player.id] = 'bench';
        return acc;
      }, {});
      setPlayerPositions(initialPositions);
    } else {
      setPlayerPositions({});
    }
  }, [selectedTeam, allPlayersForTeam]);

  const pitchPlayers = useMemo(() => allPlayersForTeam.filter(p => playerPositions[p.id] === 'pitch'), [allPlayersForTeam, playerPositions]);
  const benchPlayers = useMemo(() => allPlayersForTeam.filter(p => playerPositions[p.id] === 'bench'), [allPlayersForTeam, playerPositions]);

  const onDropPlayer = (playerId, toPosition) => {
    const clubTrainedCount = pitchPlayers.filter(p => dummyPlayers.find(dp => dp.id === p.id)?.trainingStatus === TRAINING_STATUS.CLUB).length;
    const assocTrainedCount = pitchPlayers.filter(p => dummyPlayers.find(dp => dp.id === p.id)?.trainingStatus === TRAINING_STATUS.ASSOC).length;
    const totalHomegrownCount = clubTrainedCount + assocTrainedCount;
    const homegrownDeficit = Math.max(0, UEFA_RULES.MIN_TOTAL_HOMEGROWN - totalHomegrownCount);
    const maxSquadSize = UEFA_RULES.MAX_SQUAD_SIZE - homegrownDeficit;

    if (toPosition === 'pitch' && pitchPlayers.length >= maxSquadSize && playerPositions[playerId] !== 'pitch') {
      setNotification(`Maximum squad size of ${maxSquadSize} reached.`);
      return;
    }

    setPlayerPositions(prev => ({ ...prev, [playerId]: toPosition }));
  };

  const canDropToPitch = (item, position) => {
    if (position !== 'pitch' || playerPositions[item.id] === 'pitch') return true;

    const clubTrainedCount = pitchPlayers.filter(p => p.trainingStatus === TRAINING_STATUS.CLUB).length;
    const assocTrainedCount = pitchPlayers.filter(p => p.trainingStatus === TRAINING_STATUS.ASSOC).length;
    const totalHomegrownCount = clubTrainedCount + assocTrainedCount;
    const homegrownDeficit = Math.max(0, UEFA_RULES.MIN_TOTAL_HOMEGROWN - totalHomegrownCount);
    const maxSquadSize = UEFA_RULES.MAX_SQUARE_SIZE - homegrownDeficit;

    return pitchPlayers.length < maxSquadSize;
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="bg-slate-900 text-white min-h-screen p-4 md:p-8 font-sans">
        <Notification message={notification} onDismiss={() => setNotification(null)} />
        <header className="text-center mb-8">
          <h1 className="text-4xl font-extrabold text-blue-400 tracking-wider">UEFA Squad Builder</h1>
          <p className="text-slate-400 text-lg">Detailed 2024/2025 Season Rules</p>
        </header>

        <div className="flex flex-col sm:flex-row justify-center items-center gap-4 mb-8 p-4 bg-slate-800/50 rounded-lg max-w-xl mx-auto">
          <select value={selectedLeague} onChange={e => { setSelectedLeague(e.target.value); setSelectedTeam(''); }} className="bg-slate-700 border border-slate-600 rounded-md p-2 w-full sm:w-auto focus:ring-2 focus:ring-blue-500 focus:outline-none">
            <option value="">-- Select League --</option>
            {dummyLeagues.map(l => <option key={l.id} value={l.id}>{l.name}</option>)}
          </select>
          <select value={selectedTeam} onChange={e => setSelectedTeam(e.target.value)} disabled={!selectedLeague} className="bg-slate-700 border border-slate-600 rounded-md p-2 w-full sm:w-auto disabled:opacity-50 disabled:cursor-not-allowed focus:ring-2 focus:ring-blue-500 focus:outline-none">
            <option value="">-- Select Team --</option>
            {availableTeams.map(t => <option key={t.id} value={t.id}>{t.name}</option>)}
          </select>
        </div>

        {selectedTeam ? (
          <>
            <div className="flex flex-col lg:flex-row gap-8">
              <DropZone onDropPlayer={onDropPlayer} position="pitch" title="Eligible Squad (List A)" titleColor="text-amber-400" canDrop={canDropToPitch}>
                {pitchPlayers.map(p => <Player key={p.id} player={p} />)}
              </DropZone>
              <DropZone onDropPlayer={onDropPlayer} position="bench" title="Unregistered Players" titleColor="text-slate-400" canDrop={() => true}>
                {benchPlayers.map(p => <Player key={p.id} player={p} />)}
              </DropZone>
            </div>
            <SquadStats pitchPlayers={pitchPlayers} />
          </>
        ) : (
          <div className="text-center py-16 px-4 bg-slate-800/50 rounded-lg max-w-xl mx-auto">
            <h2 className="text-2xl font-bold text-slate-300">Welcome!</h2>
            <p className="text-slate-400 mt-2">Please select a league and a team to start building your squad.</p>
          </div>
        )}
      </div>
    </DndProvider>
  );
}
